INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56461, 2, 0, 'Какая злая сила осквернила вас?', 12, 0, 100, 0, 0, 0, 55134, 0, 'Андуин Ринн to Привидение Лина Стального Кулака');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56461, 3, 0, 'Что с вами произошло?', 12, 0, 100, 0, 0, 0, 55133, 0, 'Андуин Ринн to Привидение Лина Стального Кулака');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56461, 4, 0, 'Мне все больше хочется увидеть этот Вечноцветущий дол. Я должен там побывать.', 12, 0, 100, 0, 0, 0, 58192, 0, 'Андуин Ринн to 56442');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56461, 5, 0, 'Очистись от скверны, дух.', 12, 0, 100, 0, 0, 0, 55101, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56461, 6, 0, 'Успокойся, дух.', 12, 0, 100, 0, 0, 0, 55103, 0, 'Андуин Ринн to Player');
