INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56785, 1, 0, 'Следите за движениями своих змеев. Сконцентрируйтесь и нанесите удар вместе!', 14, 0, 100, 0, 0, 0, 56182, 0, 'Лю Пламенное Сердце');
