INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57204, 0, 0, 'I only once had to do it ... I hope everything will turn out.', 12, 0, 100, 0, 0, 0, 0, 0, 'Мудрый Асе to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57204, 1, 0, 'What ... What is this place? It\'s beautiful ... but something is wrong.', 12, 0, 100, 0, 0, 0, 0, 0, 'Мудрый Асе to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 0, 0, 'До свидания, маленький друг. Очень скоро мы снова увидимся!', 12, 0, 100, 0, 0, 28586, 61625, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 1, 0, 'Еще немного нефрита, собранного с твоей помощью, и она будет закончена.', 12, 0, 100, 0, 0, 28579, 56138, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 2, 0, 'Не печалься. Жизнь продолжается. Я не являюсь ни началом ее, ни концом.', 12, 0, 100, 0, 0, 28581, 56141, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 3, 0, 'Видишь большую статую там, вдали?', 12, 0, 100, 0, 0, 28577, 56136, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 4, 0, 'Мои силы на исходе. Мне нужно отдохнуть.', 12, 0, 100, 0, 0, 28585, 56147, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 5, 0, 'Возможно, когда-нибудь тебе тоже придется встать на защиту того, что дорого для тебя.', 12, 0, 100, 0, 0, 28582, 56140, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 6, 0, 'Мы живем вместе и умираем вместе. Все в Пандарии связано между собой.', 12, 0, 100, 0, 0, 28584, 56928, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 7, 0, 'Жрецы храма слишком напуганы, чтобы взглянуть правде в лицо...', 12, 0, 100, 0, 0, 28575, 56157, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 8, 0, 'Скульпторы много лет усердно трудились над ней, и вот она почти готова.', 12, 0, 100, 0, 0, 28578, 56137, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 9, 0, 'С моим последним вздохом, моя жизненная сущность перенесется в статую. Так появится новый страж.', 12, 0, 100, 0, 0, 28580, 56139, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 10, 0, 'Когда придет такой день, вспомни о свете и всех чудесах этого мира, и сражайся.', 12, 0, 100, 0, 0, 28583, 56927, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57286, 11, 0, 'Я очень стара. Мои дни в этом мире почти подошли к концу.', 12, 0, 100, 0, 0, 28576, 56135, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 0, 0, 'Говори что хочешь. Я не сдвинусь с места.', 12, 0, 100, 0, 0, 0, 56588, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 1, 0, 'Не-е-ет!', 12, 0, 100, 0, 0, 0, 123493, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 2, 0, 'Ты совсем без ума уже! Вот еще один!', 12, 0, 100, 0, 0, 0, 56218, 0, 'И-Мо Длинная Бровь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 3, 0, 'Что за?..', 12, 0, 100, 0, 0, 0, 56215, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 4, 0, 'Ой! Ты что это делаешь?', 12, 0, 100, 0, 0, 0, 56212, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 5, 0, 'Ох... Голова кружится.', 12, 0, 100, 0, 0, 0, 56217, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 6, 0, 'А вот и они...', 12, 0, 100, 0, 0, 0, 85949, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 7, 0, 'Уходи отсюда, или они заберут и тебя!', 12, 0, 100, 0, 0, 0, 56216, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 8, 0, 'Эй, хватит! Я жду, пока меня заберут буревестники!', 12, 0, 100, 0, 0, 0, 56213, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57310, 9, 0, 'Держитесь, буревестники! Летите сюда, заберите меня с собой!', 12, 0, 100, 0, 0, 0, 56853, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57400, 0, 0, 'Гони их прочь!', 12, 0, 100, 0, 0, 0, 63414, 0, 'Страж Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57400, 1, 0, 'Я достиг просветления. Спасибо, |3-6($r).', 12, 0, 100, 0, 0, 0, 63409, 0, 'Страж Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57405, 0, 0, 'Enjoy your tour of Pandaria\'s most ancient and respected silk farm.', 12, 0, 100, 0, 0, 0, 56524, 0, 'Silkmaster Tsai to Player');
