INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58898, 3, 0, 'Только не лицо!', 12, 0, 100, 0, 0, 29512, 58009, 0, 'Бдительный часовой to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58898, 4, 0, 'Кто это сделал? А ну, признавайтесь! Кто это был?', 12, 0, 100, 0, 0, 0, 58010, 0, 'Бдительный часовой to Player');
