INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58959, 0, 0, 'Эй, $n! Помнишь меня? Ладно, ладно... Эй, послушай... Мы совсем не обязаны помогать этому парню...', 12, 0, 100, 0, 0, 0, 69547, 0, 'Кеппико to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58959, 1, 0, 'В этом месте наверняка есть, чем поживиться... Ну же, $gначальник:начальница,! Тебе же этого хочется...', 12, 0, 100, 0, 0, 0, 69548, 0, 'Кеппико to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58961, 0, 0, 'Кто смеет бросать мне вызов?', 12, 0, 100, 0, 0, 0, 82437, 0, 'Батаарский яунгол to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58968, 0, 0, 'How can I give up when heroes still fight at my side?', 12, 0, 100, 0, 0, 0, 57817, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58968, 0, 1, 'I... I don\'t know what came over me.', 12, 0, 100, 0, 0, 0, 57812, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58968, 0, 2, 'It seems we haven\'t lost yet.', 12, 0, 100, 0, 0, 0, 57818, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58968, 0, 3, 'It was... so dark...', 12, 0, 100, 0, 0, 0, 57819, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58968, 0, 4, 'No, I cannot let it end like this. I have to keep fighting!', 12, 0, 100, 0, 0, 0, 57816, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58968, 0, 5, 'Of course. We must not lose hope.', 12, 0, 100, 0, 0, 0, 57813, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58968, 0, 6, 'So they CAN die... I have to stay focused.', 12, 0, 100, 0, 0, 0, 57814, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58968, 0, 7, 'Thank you, I had lost sight of the Way.', 12, 0, 100, 0, 0, 0, 57815, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58969, 0, 2, 'How can I give up when heroes still fight at my side?', 12, 0, 100, 0, 0, 0, 57817, 0, 'Hopeless Acolyte');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58969, 1, 0, 'I... I don\'t know what came over me.', 12, 0, 100, 0, 0, 0, 57812, 0, 'Hopeless Acolyte');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58969, 2, 1, 'Thank you, I had lost sight of the Way.', 12, 0, 100, 0, 0, 0, 57815, 0, 'Hopeless Acolyte');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 0, 0, 'Еще сауроки!', 12, 0, 100, 25, 0, 0, 58041, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 1, 0, 'Неправильный ответ.', 12, 0, 100, 0, 0, 0, 57915, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 2, 0, 'Берегись!', 14, 0, 100, 0, 0, 0, 143886, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 3, 0, 'Стоп... Не нравится мне это...', 12, 0, 100, 0, 0, 0, 57943, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 4, 0, 'Не отходите от меня. И будьте начеку.', 12, 0, 100, 0, 0, 0, 57917, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 5, 0, 'Мое колено! Прости, я не могу сражаться.', 12, 0, 100, 0, 0, 0, 58042, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 6, 0, 'Спасибо. Не беспокойся обо мне, нам нужно двигаться дальше.', 12, 0, 100, 1, 0, 0, 58040, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 7, 0, 'Похоже, нам придется продвигаться вслепую. Хорошо, что ты с нами.', 12, 0, 100, 1, 0, 0, 57916, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 8, 0, '$p, помоги... Мне... стрела попала в колено.', 12, 0, 100, 0, 0, 0, 58039, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 9, 0, 'Я ранен... Осторожнее!', 14, 0, 100, 0, 0, 0, 58003, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 10, 0, 'Лагерь уже близко. Мы сможем сами добраться.', 12, 0, 100, 0, 0, 0, 58043, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58978, 11, 0, 'Это твой последний шанс, ящерица. Говори, где ваши патрули?!', 12, 0, 100, 0, 0, 0, 57914, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59019, 0, 0, '$gОн:Она,, кажется, $gсильный:сильная,. Может, у $gнего:нее, хватит силы прогнать яунголов.', 12, 0, 100, 273, 0, 0, 58429, 0, 'Житель деревни Бинан to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59021, 0, 0, 'Никто не устоит против огня яунголов!', 12, 0, 100, 0, 0, 0, 64509, 0, 'Батаарский заклинатель пламени to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59037, 0, 0, 'Кун-Динь набросился на вас! Сражайтесь! |TInterface\\Icons\\ability_warrior_secondwind.blp:20|t', 42, 0, 100, 0, 0, 0, 59814, 0, 'Кун-Динь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59040, 0, 0, 'Ты что тут делаешь?! Ты что, не знаешь, что яунголы начали вторжение на вершину Кунь-Лай?', 12, 0, 100, 0, 0, 0, 57973, 0, 'Куньлайский беженец to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59055, 0, 0, 'Может быть, $gон:она, герой, пришедший из-за туманов, чтобы спасти нас?', 12, 0, 100, 0, 0, 0, 58012, 0, 'Куньлайский беженец to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59067, 0, 0, 'Пока вы можете остаться, но когда раны Тейлора затянутся, вам придется уйти.', 12, 0, 100, 1, 0, 30631, 58016, 0, 'Здоровяк Кажаши to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59067, 1, 0, 'Погоди! Я чувствую присутствие чужаков. Хозены? А ну выходите!', 12, 0, 100, 0, 0, 30628, 58013, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59067, 2, 0, 'Я не при чем. Это все Гукгут, вождь племени Ловкого Хвоста.', 12, 0, 100, 0, 0, 30629, 58014, 0, 'Здоровяк Кажаши to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59067, 3, 0, 'Мы не любим, когда в нашу деревню заходят чужаки.', 12, 0, 100, 0, 0, 30630, 58015, 0, 'Здоровяк Кажаши to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59067, 4, 0, 'Только без резких движений.', 12, 0, 100, 1, 0, 30633, 61289, 0, 'Здоровяк Кажаши to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59068, 0, 0, 'У нас нет выбора. Убери оружие, воин, и мы сделаем то же самое.', 12, 0, 100, 0, 0, 27707, 58017, 0, 'Релл Ночной Ветер to Мишка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59069, 0, 0, 'Что ты сделал с адмиралом?', 12, 0, 100, 0, 0, 27847, 58020, 0, 'Салли "Рассольный" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59070, 0, 0, 'Командир, вы думаете, мы можем ему верить?', 12, 0, 100, 0, 0, 27660, 58019, 0, 'Мишка to Здоровяк Кажаши');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59073, 0, 0, 'Добро пожаловать в деревню Бинан, |3-6($c). Умоляю, помоги нам.', 12, 0, 100, 20, 0, 0, 58440, 0, 'Мэр Ежевичный Посох to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59073, 1, 0, 'Ты $gспас:спасла, деревню Бинан, $n. Ударьте в гонг, чтобы все знали об этом!', 12, 0, 100, 0, 0, 0, 58467, 0, 'Мэр Ежевичный Посох to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59073, 2, 0, 'You have saved Binan Village, $n. Ring the gong so all shall know!', 12, 0, 100, 0, 0, 0, 58467, 0, 'Mayor Bramblestaff to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59073, 3, 0, 'Welcome to Binan Village, $c. I beg you... please help us.', 12, 0, 100, 20, 0, 0, 58440, 0, 'Mayor Bramblestaff to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59075, 0, 0, 'Интересно, есть ли что-нибудь, чего этот лекарь не умеет?', 12, 0, 100, 1, 0, 28030, 63210, 0, 'Хмелевар-предок to Булькающий бражный хмелементаль');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59075, 1, 0, 'Эй... эй, ТЫ! Это НАШИ... летающие... пивные монстры?', 14, 0, 100, 1, 0, 27510, 63137, 0, 'Хмелевар-предок to Тучный бражный хмелементаль');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59076, 0, 0, 'Думаю, этого более чем достаточно. Скорее, $n, я уже вижу, как на горизонте собираются новые отряды яунголов.', 12, 0, 100, 396, 0, 0, 58510, 0, 'Командир Се to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59076, 1, 0, 'Looks like you got more than enough. Quick, $n, I think I can see the yaungol reinforcements massing on the horizon.', 12, 0, 100, 396, 0, 0, 58510, 0, 'Commander Hsieh to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59077, 0, 0, '$c, приветствую тебя. Твои друзья внутри. Но сначала могу я попросить кое о чем?', 12, 0, 100, 1, 0, 0, 58493, 0, 'Аптекарь Чэн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59077, 1, 0, 'Greetings, $c. Your people are inside. But first, may I ask a favor?', 12, 0, 100, 1, 0, 0, 58493, 0, 'Apothecary Cheng to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59083, 0, 0, 'Глупцы! Сражайтесь! Не дайте этим жалким пандаренам победить вас!', 14, 0, 100, 0, 0, 0, 62838, 0, 'Батаарский огненный воин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59083, 1, 0, 'Fools! Fight harder! Do not let these puny pandaren defeat you!', 14, 0, 100, 0, 0, 0, 62838, 0, 'Bataari Fire-Warrior to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59083, 2, 0, 'Я сокрушу тебя!', 12, 0, 100, 0, 0, 0, 117447, 0, 'Батаарский огненный воин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59098, 0, 0, 'Х-х-хe-хe-хе! Ты сдохнешь в грязи!', 12, 0, 100, 0, 0, 0, 58069, 0, 'Налетчик Речного Клинка to Player');
