INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59441, 0, 0, 'Kun-Lai Summit is where you\'re going to die, orc. I\'m going to bury you.', 12, 0, 100, 15, 0, 32449, 58616, 0, 'Admiral Taylor to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59442, 0, 0, 'As soon as I... can get up, I\'m going to crush you!', 12, 0, 100, 5, 0, 29184, 58620, 0, 'General Nazgrim to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59442, 1, 0, 'Good work, friend. Speak with Kiryn and Rivett while I assess our new surroundings.', 12, 0, 100, 1, 0, 0, 60066, 0, 'General Nazgrim to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59442, 3, 0, 'I cannot emphasize enough how important it is that you kill those channelers and bring that shield down, $n.', 12, 0, 100, 1, 0, 0, 60065, 0, 'General Nazgrim to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59442, 6, 0, 'Alright, recruits! Thanks to $p, we\'re going to reclaim Kun-Lai summit from the Yaungol and make it safe again. For the Horde!', 14, 0, 100, 0, 0, 0, 65829, 0, 'General Nazgrim to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59442, 7, 0, 'You\'ve done great work here, friend. We should have a strong base of operations here going forward - see if anyone else needs your help.', 12, 0, 100, 0, 0, 0, 65830, 0, 'General Nazgrim to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59448, 0, 0, 'Heya, $n. Nice work here, chief.', 12, 0, 100, 1, 0, 0, 60051, 0, 'Rivett Clutchpop to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59448, 1, 0, 'Good to hear you got those farmers all squared away, chief. Now we gotta take care of some new business.', 12, 0, 100, 1, 0, 0, 60054, 0, 'Rivett Clutchpop to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59448, 2, 0, 'You gotta blow those oil rigs, $n. We don\'t want them stinky yaungol raining exploding fire kegs down on our heads!', 12, 0, 100, 1, 0, 0, 60055, 0, 'Rivett Clutchpop to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59448, 3, 0, 'What gives? You gotta get to that farm. Did Kiryn send you to do her work? She\'s so lazy!', 12, 0, 100, 1, 0, 0, 60052, 0, 'Rivett Clutchpop to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59448, 4, 0, 'Что такое? Тебе надо добраться до этой фермы. Кирин что, послала тебя вместо себя? Вот лентяйка!', 12, 0, 100, 1, 0, 0, 60052, 0, 'Риветт Сталехват to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59448, 5, 0, 'The sound of those explosions was like music to my ears. Come here, $n. Give me a hug!', 12, 0, 100, 1, 0, 0, 60056, 0, 'Rivett Clutchpop to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59448, 6, 0, 'Рад слышать, что ты $gразобрался:разобралась, с этими фермерами, $gначальник:леди,. Теперь нужно заняться кое-чем еще.', 12, 0, 100, 1, 0, 0, 60054, 0, 'Риветт Сталехват to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59448, 7, 0, 'Звук этих взрывов мелодично звучит у меня в ушах. Иди сюда, $n. Давай обнимемся!', 12, 0, 100, 1, 0, 0, 60056, 0, 'Риветт Сталехват to Player');
