INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59827, 0, 0, 'Оружие – продолжение бойца. Заботься о нем так, как о себе, и оно никогда тебя не подведет.', 12, 0, 100, 0, 0, 0, 59359, 0, 'Ковщик Шоу to Player');
