INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59894, 1, 0, 'Ур-ра! А они еще говорят, что Ячья подкова – не удачка! Огромное спасибо за спасение.', 12, 0, 100, 0, 0, 0, 60387, 0, 'Брат Ячья Подкова to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59894, 2, 0, 'Если у вас есть оружие дальнего боя, используйте его, чтобы перебить веревки, удерживающие груммелей.', 12, 0, 100, 0, 0, 0, 60388, 0, 'Брат Ячья Подкова to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (59958, 0, 0, 'Новый король, новые правила. Прости, но шнуропсы надо ограбляй!', 12, 0, 100, 0, 0, 0, 59810, 0, 'Так-Так to Player');
