INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 2, 0, 'Ну что мы вам сделали?', 12, 0, 100, 0, 0, 0, 59575, 0, 'Пандарен-пленник');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 3, 0, 'Прошу, |3-6($c), выгони их с Кунь-Лай навсегда.', 12, 0, 100, 2, 0, 0, 59592, 0, 'Пандарен-пленник to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 4, 0, 'Мне нужно найти свою семью!', 12, 0, 100, 94, 0, 0, 59591, 0, 'Пандарен-пленник to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 5, 0, 'There\'s a whole bunch of them still in the caves.  They\'re getting ready to attack your people!', 12, 0, 100, 4, 0, 0, 59590, 0, 'Pandaren Prisoner to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 6, 0, 'I need to find my family!', 12, 0, 100, 94, 0, 0, 59591, 0, 'Pandaren Prisoner to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 7, 0, 'Thank you, $c.  Thank you!', 12, 0, 100, 21, 0, 0, 59587, 0, 'Pandaren Prisoner to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 8, 0, 'Please, would you get me out of here?', 12, 0, 100, 0, 0, 0, 59568, 0, 'Pandaren Prisoner');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 9, 0, 'Hey you. Would you let me loose?', 12, 0, 100, 0, 0, 0, 59573, 0, 'Pandaren Prisoner');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 10, 0, 'Спасибо, |3-6($c). Спасибо!', 12, 0, 100, 11, 0, 0, 59587, 0, 'Пандарен-пленник to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 11, 0, 'Blessings of the four winds upon you, stranger.', 12, 0, 100, 94, 0, 0, 59593, 0, 'Pandaren Prisoner to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 12, 0, 'В пещерах их еще много. Они готовятся напасть на вас!', 12, 0, 100, 17, 0, 0, 59590, 0, 'Пандарен-пленник to Player');
