INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60229, 0, 0, 'I\'m not leaving these behind. This is my family\'s legacy!', 12, 0, 0, 1, 0, 0, 59726, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60234, 0, 0, '$n, was it? There\'ll be a few extra gold for you if you can personally guarantee my safety.', 12, 0, 0, 1, 0, 0, 59732, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60235, 0, 0, 'You\'re... working for the Shado-Pan? I can\'t believe it!', 12, 0, 0, 1, 0, 0, 59733, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60236, 0, 0, 'When these monsters came, the Shado-Pan got so serious... they kept saying, "Sha". What\'s that?', 12, 0, 0, 1, 0, 0, 59734, 0, 'KillCreditText');
