INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60370, 0, 0, 'Сердитый хозен наблюдай за тобой, бортель.', 15, 0, 100, 0, 0, 0, 59782, 0, 'Тотем разгневанной обезьяны to Player');
