INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60767, 2, 0, 'Угадаешь, кто из нас настоящий?', 14, 0, 100, 0, 0, 0, 60244, 0, 'Капитан Живорез');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60767, 3, 0, 'Как ты... уга... $gдал:дала,...', 12, 0, 100, 0, 0, 0, 60245, 0, 'Капитан Живорез to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60767, 4, 0, 'Ха! Посмотрим, угадаешь ли ты!', 14, 0, 100, 0, 0, 0, 60227, 0, 'Капитан Живорез');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60769, 0, 0, 'Проснитесь, мои помощники! Полакомьтесь их плотью!', 14, 0, 100, 1, 0, 0, 62650, 0, 'Генерал Шо Лень to 60765');
