INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60902, 0, 0, 'Видишь ли, своим поступком ты $gподдержал:поддержала, самую ценную для нас, пандаренов, истину.', 12, 0, 100, 0, 0, 0, 60371, 0, 'Мудрец Ляо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60951, 0, 0, 'Они боролись за еду. И вместо того чтобы делиться, кто-то ее просто испортил. Но зачем?', 12, 0, 100, 16, 0, 0, 60474, 0, 'Сяо Ту to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60951, 1, 0, 'Насколько я понимаю, в этом бою каждый был сам за себя. Зачем яунголам убивать друг друга просто так?', 12, 0, 100, 16, 0, 0, 60475, 0, 'Сяо Ту to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60951, 2, 0, 'Это вождь яунголов. Его убили, как и всех остальных. Похоже, в последнем бою он не сумел сохранить порядок.', 12, 0, 100, 16, 0, 0, 60476, 0, 'Сяо Ту to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60951, 3, 0, 'Смотри-ка, вещи Суны! Должно быть, она решила расположиться здесь на ночлег. Но где она сама?', 12, 0, 100, 16, 0, 0, 60700, 0, 'Сяо Ту to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 0, 0, 'Ты ищешь оружие грозового бога? Выйди вперед, шаман.', 12, 0, 100, 0, 0, 60621, 102458, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 1, 0, 'Многие жаждали завладеть артефактами Хранителя бурь. Но испытаний не прошел никто. Иди же и повстречайся со своей судьбой!', 12, 0, 100, 0, 0, 60624, 102356, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 2, 0, 'Первый бой – испытание силы. Победителем выйдет только один. Скажи, когда приготовишься начать.', 12, 0, 100, 0, 0, 60612, 102465, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 3, 0, 'Шаман победил! Первое испытание окончено.', 12, 0, 100, 0, 0, 60619, 102472, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 4, 0, 'Настоящий герой никогда не сражается в одиночку. Следующее испытание – работа в команде. Выбери себе напарника.', 12, 0, 100, 0, 0, 60626, 102475, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 5, 0, 'Помните! Чтобы победить, вы должны действовать как одна команда. 3... 2... 1... В бой!', 12, 0, 100, 0, 0, 60633, 102483, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 6, 0, 'Поздравляю! Второе испытание пройдено.', 12, 0, 100, 0, 0, 60644, 102495, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 7, 0, 'Твое мастерство несомненно. Теперь тебя ждет последнее испытание, которое еще никому не удавалось пройти.', 12, 0, 100, 0, 0, 60648, 102499, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 8, 0, 'Оружие Хранителя бурь так могущественно, что даже Властелин Грома не посмел прикоснуться к нему.', 12, 0, 100, 0, 0, 60649, 102500, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 9, 0, 'Кулак Ра-дена станет твоей последней проверкой. Возьми его – и если ты выживешь, это оружие будет служить тебе верой и правдой.', 12, 0, 100, 0, 0, 60650, 102501, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 10, 0, 'Что-то не так... Здесь кто-то чужой...', 12, 0, 100, 0, 0, 60661, 102520, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 11, 0, 'Тебе удалось всех нас спасти...', 12, 0, 100, 0, 0, 60608, 102545, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 12, 0, 'Больше испытаний не требуется, шаман. Оружие Хранителя бурь нашло себе $gнового хозяина:новую хозяйку,.', 12, 0, 100, 0, 0, 60609, 102547, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60968, 13, 0, 'Ступай же, последователь Служителей Земли, я благословляю тебя. Азероту нужно отразить удар, и такие герои, как ты, должны принять вызов.', 12, 0, 100, 0, 0, 60611, 109902, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60970, 3, 0, 'Ну, теперь мы хотя бы знаем, что Андуин жив. Отлично, $n. А сейчас поговори со старейшиной Лушанем. Он уже справлялся о тебе.', 12, 0, 100, 0, 0, 0, 61205, 0, 'Адмирал Тейлор to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60973, 0, 0, 'I\'\'ve never performed this ritual. I don\'\'t know what will happen, exactly.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60973, 1, 0, 'It\'\'s been passed down from ancient times, but none of our people have needed to use it since then.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60973, 2, 0, 'Let us see if this is sufficient to cleanse the corruption from my people\'\'s souls.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60973, 3, 0, 'It worked! It wo-', 12, 0, 100, 0, 0, 0, 60999, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60973, 4, 0, 'Wh... what is happening why is the ritual affecting her?', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60973, 5, 0, 'Whar is that?', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60978, 0, 0, 'At last, $n, we get o face each other in battle!', 12, 0, 100, 1, 0, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60978, 1, 0, 'Wow that hurt! Well fought, friend.', 12, 0, 100, 1, 0, 0, 60640, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60979, 0, 0, 'Ken-Ken\'s got this!', 12, 0, 100, 1, 0, 0, 60739, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60979, 1, 0, 'Ken-Ken surrenders!', 12, 0, 100, 1, 0, 0, 0, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60980, 0, 0, 'We can win if we work together.', 12, 0, 100, 1, 0, 0, 60737, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60980, 1, 0, 'Well... done... champions.', 12, 0, 100, 1, 0, 0, 60669, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60994, 0, 0, 'Round one... FIGHT!', 12, 0, 100, 1, 0, 0, 60659, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60994, 1, 0, 'Defeated!', 12, 0, 100, 1, 0, 0, 60663, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60995, 0, 0, 'Я тебя уничтожу!', 12, 0, 100, 0, 0, 0, 127505, 0, 'Разъяренный захватчик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60995, 1, 0, 'Ар-р-р!', 12, 0, 100, 0, 0, 0, 82862, 0, 'Разъяренный захватчик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60996, 0, 0, 'This is going to be easier than a drinking contest with a grummle.', 12, 0, 100, 0, 0, 0, 60651, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60996, 1, 0, 'I surrender! You win.', 12, 0, 100, 0, 0, 0, 60650, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61004, 0, 0, 'Tankiss... too... squishy...', 12, 0, 100, 1, 0, 0, 61131, 0, 'EvEndText text');
