INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61580, 0, 0, 'Подойди, |3-6($r). Я хочу поговорить с тобой. Нашу тайну больше нельзя скрывать.', 12, 0, 100, 0, 0, 0, 61987, 0, 'Старейшина Ого to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61582, 0, 0, 'Сработало! Срабо...', 12, 0, 100, 0, 0, 0, 60999, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61582, 1, 0, 'Что это?!', 12, 0, 100, 0, 0, 0, 105272, 0, 'Говорящий с водой Гожай to Мастер клинков Мэй');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61582, 2, 0, 'Давай посмотрим, хватит ли этого, чтобы очистить души моего народа от скверны.', 12, 0, 100, 0, 0, 0, 60993, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61582, 3, 0, 'Что... Что происходит? Почему на нее действует ритуал?', 12, 0, 100, 0, 0, 0, 61000, 0, 'Говорящий с водой Гожай to Loremaster Staff Bunny Event Version');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61582, 4, 0, 'Я никогда не проводил этот ритуал. Не знаю, что точно произойдет.', 12, 0, 100, 0, 0, 0, 60991, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61582, 5, 0, 'Когда я начал ритуал, из тебя выскочила тварь. И сдохла от рук |3-1($p)!', 12, 0, 100, 0, 0, 0, 61011, 0, 'Говорящий с водой Гожай to Мастер клинков Мэй');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61582, 6, 0, 'Эта... штука, похоже, и управляла жителями деревни! Теперь я знаю, что делать.', 12, 0, 100, 0, 0, 0, 61008, 0, 'Говорящий с водой Гожай to Мастер клинков Мэй');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61582, 7, 0, 'Это очень древний ритуал, но за все это время он ни разу не пригодился моему народу.', 12, 0, 100, 0, 0, 0, 60992, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61585, 0, 0, 'Попытайся понять, груммель. Это храм Нюцзао, великого черного быка. Мы его слуги и хранители этого храма. Мы не "заменяем яков".', 12, 0, 100, 0, 0, 0, 61113, 0, 'Сторож яков Киана to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61585, 1, 0, 'Хорошо. У озера к северу отсюда пасется много диких яков. Можете "заменить" своего яка на одного из них.', 12, 0, 100, 0, 0, 0, 61117, 0, 'Сторож яков Киана to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61585, 2, 0, 'Кажется, ты не понимаешь, как ведутся дела в храме яков.', 12, 0, 100, 0, 0, 0, 61111, 0, 'Сторож яков Киана to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61597, 0, 0, 'Превосходно! Провозглашаю тебя победителем в Состязании Силы! Пусть враги трепещут перед тобой.', 14, 0, 100, 1, 0, 29026, 61101, 0, 'Сюэнь to Player');
