INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63071, 3, 0, 'Welche Sorte Tests?', 12, 0, 100, 6, 0, 0, 66297, 0, 'Skeer der Blutsucher to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63072, 0, 0, 'Gewebeproben?', 12, 0, 100, 6, 0, 0, 66300, 0, 'Rik\'kal der Sezierer to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63072, 1, 0, 'Nur etwas Zellentraumainduktion, vielleicht die eine oder andere Gewebeprobe, etwas Mikroschallgensondierung. Nichts besonders Blutiges!', 12, 0, 100, 396, 0, 0, 66298, 0, 'Rik\'kal der Sezierer to Player');
