INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 0, 0, 'Hey there. I don\'t think you want to go down that tunnel on foot. The saurok are out in force today and they are mighty territorial.', 12, 0, 100, 0, 0, 0, 63820, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 1, 0, 'Ever been to Kun-Lai before? It\'s great. Long flowing farmlands. Beautiful mountains. And absolutely nothing dangerous whatsoever.', 12, 0, 100, 0, 0, 0, 63848, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 2, 0, 'Well... yes. There are problems with the hozen.', 12, 0, 100, 0, 0, 0, 63849, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 3, 0, 'Yes, and the tigers.', 12, 0, 100, 0, 0, 0, 63855, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 4, 0, 'The spears are just for show. As long as we don\'t get out of the boat or make eye contact or get hit by a spear then we should be fine.', 12, 0, 100, 0, 0, 0, 63850, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 5, 0, 'It\'s safe. I brought Lorewalker Cho and his strange companions up this way not long back.', 12, 0, 100, 0, 0, 0, 63851, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 6, 0, 'You had that coming. Waving your incense about in his face... wait a minute. Something doesn\'t feel right about this.', 12, 0, 100, 0, 0, 0, 63852, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 7, 0, 'Are those refugees? What\'s going on over there? Do I hear fighting?', 12, 0, 100, 0, 0, 0, 63853, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63367, 8, 0, 'These people are farmers, not fighters! They may need our help!', 12, 0, 100, 0, 0, 0, 63854, 0, 'Brewmaster Boof to Player');
