INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63499, 0, 0, 'Мой отец думает, что женщине опасно выходить за пределы Цзоучин. Он отпускает меня только за припасами из Пэй-Лэй.', 12, 0, 100, 1, 0, 0, 62927, 0, 'Шоми to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63499, 1, 0, 'Надеюсь, ты не будешь против, если мы воспользуемся шаром вместе. Отец мне не разрешает разговаривать с незнакомцами, но ты, кажется, хороший человек.', 12, 0, 100, 1, 0, 0, 62925, 0, 'Шоми to Player');
