INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64191, 0, 0, 'I\'ll see you soon, $n.', 12, 0, 100, 3, 0, 0, 111948, 0, 'Ghostly Pandaren Craftsman');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64191, 0, 1, 'Until we meet again, $n.', 12, 0, 100, 3, 0, 0, 63912, 0, 'Ghostly Pandaren Craftsman');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64202, 0, 0, 'Уйди-ка ты с дороги, слышь?', 12, 0, 100, 1, 0, 0, 64656, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64202, 1, 0, 'Зандалары вернут прежние дни.', 12, 0, 100, 274, 0, 0, 64661, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64202, 2, 0, 'Попробуй магию зандаларов!', 12, 0, 100, 274, 0, 0, 64657, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64202, 3, 0, 'Зандалары снова поднимутся.', 12, 0, 100, 1, 0, 0, 64655, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64202, 4, 0, 'Ты будешь моей добычей.', 12, 0, 100, 1, 0, 0, 64660, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64202, 5, 0, 'Против зандаларов и могу сразу? Кишка тонка.', 12, 0, 100, 0, 0, 0, 64662, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64231, 0, 0, 'Если ты хочешь улучшить свои навыки в кулинарии, я могу поделиться с тобой своими знаниями.', 12, 0, 100, 0, 0, 0, 68293, 0, 'Суншинь Железная Лапа to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64231, 1, 0, 'Эй, $gпутешественник:путешественница,! Хочешь научиться готовить?', 12, 0, 100, 0, 0, 0, 68288, 0, 'Суншинь Железная Лапа to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64236, 0, 0, 'У всех есть кристаллы?', 12, 0, 100, 1, 0, 0, 61997, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64240, 0, 0, 'Don\'t hold back.', 12, 0, 100, 1, 0, 0, 63750, 0, 'Gossip text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64241, 1, 0, 'The mogu are attack the temple!', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64243, 0, 0, 'По тайной лестнице сбегает саурок и тащит кое-какие сокровища!', 41, 0, 100, 0, 0, 0, 64421, 0, 'Глинтрок-разведчик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64259, 0, 0, 'Pick any of these fabolous rods and CAREFULLY tug the line. That should help set the hook into whatever\'s flailin\' around at the other end.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64280, 0, 0, 'Я призываю воды сссвоего родного края... Доставьте ко мне моих врагов!', 14, 0, 100, 0, 0, 0, 64153, 0, 'Маки Водорез to Маки Водорез');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64281, 0, 0, 'Фум-м!', 12, 0, 100, 2, 0, 0, 65569, 0, 'Сатай Бюй to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64283, 0, 0, '%s начинает преследовать |3-3($n)!', 41, 0, 100, 0, 0, 31564, 49578, 0, 'Янтарная сфера to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64330, 0, 0, 'Думаешь, сможешь одолеть меня в битве? Многие маленькие девочки боятся змей, а по-моему, они такие лапочки!', 12, 0, 100, 0, 0, 0, 64260, 0, 'Джулия Стивенс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64331, 0, 0, 'Нет, милый. Тебе нужно вырасти большим и сильным. Ты ведь хочешь стать таким же сильным, как я?', 12, 0, 100, 0, 0, 0, 64245, 0, 'Бри Лан');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64367, 0, 0, 'Сущность Юй-лун тепло улыбается вам, растворяясь в небе.', 16, 0, 100, 0, 0, 0, 72870, 0, 'Невидимка to Невидимка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64367, 1, 0, 'Сущность Сюэня издает триумфальный рык и превращается в небесную пыль.', 16, 0, 100, 0, 0, 0, 72876, 0, 'Невидимка to Невидимка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64367, 2, 0, 'Сущность Чи-Цзи рассеивается, оставляя за собой след из звездной пыли.', 16, 0, 100, 0, 0, 0, 72869, 0, 'Невидимка to Невидимка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64367, 3, 0, 'Сущность Нюцзао рокочет, принимая вашу помощь.', 16, 0, 100, 0, 0, 0, 72871, 0, 'Невидимка to Невидимка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64451, 0, 0, 'Купи яблочко! Яблоко в день убережет от жрецов.', 12, 0, 100, 0, 0, 0, 64642, 0, 'Юнь Яблочная Бочка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64467, 0, 0, 'Эй, $p, выход здесь.', 12, 0, 100, 0, 0, 0, 64860, 0, 'Лучница Ли to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64473, 0, 0, 'Do not worry, I will go easy on you.', 12, 0, 100, 1, 0, 0, 62546, 0, 'Gossip text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64482, 0, 0, 'Нам нужна пинта холодного пива! Сейчас же!', 14, 0, 100, 0, 0, 0, 66666, 0, 'Целительница Нань');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64482, 1, 0, 'Тише, жрец. Ты сейчас никому не сможешь помочь, у тебя сломаны обе руки! Я принесу тебе лекарство.', 12, 0, 100, 0, 0, 0, 66660, 0, 'Целительница Нань');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64492, 0, 0, 'Все готово? Отлично. Иди сюда.', 12, 0, 100, 273, 0, 30054, 64697, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64492, 1, 0, 'Для начала выпей это.', 12, 0, 100, 432, 0, 30056, 64702, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64492, 2, 0, 'Чтобы увидеть то, что может показать тебе хмель глубоких раздумий, тебе надо обрести внутреннюю гармонию.', 12, 0, 100, 0, 0, 0, 64699, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64517, 0, 0, '$p, сюда! К мосту!', 12, 0, 100, 1, 0, 0, 64811, 0, 'Шадо-мастер Чум Киу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64547, 0, 0, 'Я слышал, властитель хочет заставить наc сражаться друг с другом. Но для этого, кажется, и стараться особо не надо.', 12, 0, 100, 5, 0, 0, 61320, 0, 'Гуртанский задира');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64547, 1, 0, 'Как ты думаешь, кого выберет король?', 12, 0, 100, 1, 0, 0, 61315, 0, 'Гуртанский задира');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64547, 2, 0, 'Наш клан – самый великий на этой земле, он должен выбрать нас!', 12, 0, 100, 23, 0, 0, 61316, 0, 'Гуртанский задира');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64548, 0, 0, 'Другие кланы, небось, и магией уже не владеют. Что от них толку королю?', 12, 0, 100, 11, 0, 0, 61317, 0, 'Рубака клана Каргеш');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64548, 1, 0, 'Как ты думаешь, кого выберет король?', 12, 0, 100, 1, 0, 0, 61315, 0, 'Рубака клана Каргеш');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64548, 2, 0, 'Когда в одном месте столько кланов, драки не избежать.', 12, 0, 100, 274, 0, 0, 61319, 0, 'Рубака клана Каргеш');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64549, 0, 0, 'На крышах появляются ледяные стрелки Шадо-Пан.', 41, 0, 100, 0, 0, 0, 64855, 0, 'Ледяной стрелок Шадо-Пан to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64549, 1, 0, 'Стрелки Шадо-Пан исчезают!', 41, 0, 100, 0, 0, 0, 59331, 0, 'Ледяной стрелок Шадо-Пан to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64561, 0, 0, 'Не сегодня, малыш. У нас еще куча поручений.', 12, 0, 100, 0, 0, 0, 65057, 0, 'Цзун Мин-Иу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64561, 1, 0, 'Not today, little one. We have errands to run.', 12, 0, 100, 0, 0, 0, 65057, 0, 'Jong Ming-Yiu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 0, 0, 'Смерть Гандлингу! Умри!', 14, 0, 100, 0, 0, 29469, 65149, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 1, 0, 'Его нужно остановить! Забудь о ней!', 14, 0, 100, 0, 0, 0, 65221, 0, 'Говорящий череп to Лилиан Восс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 2, 0, 'Вот и все. Проклятие снято. Спасибо тебе, $p. Спасибо.', 12, 0, 100, 1, 0, 0, 65225, 0, 'Призрак лорда Алексея Барова to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 3, 0, 'Готовься к смерти, злодей!', 14, 0, 100, 0, 0, 27477, 65253, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 4, 0, 'Говорящий Череп передвигается в следующий зал, не говоря ни слова.', 16, 0, 100, 0, 0, 30288, 65086, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 5, 0, 'Говорящий Череп весело хихикает, глядя на гибель души инструктора.', 16, 0, 100, 11, 0, 29456, 65077, 0, 'Говорящий череп to Инструктор Ледяное Сердце');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 6, 0, 'Вперед!', 12, 0, 100, 0, 0, 0, 131943, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 7, 0, 'Наконец-то.', 12, 0, 100, 0, 0, 0, 136180, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 8, 0, 'Мы идем за тобой, Ледяное Сердце! Время платить по счетам!', 14, 0, 100, 15, 0, 0, 65074, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 9, 0, 'Невыносимое зрелище.', 12, 0, 100, 274, 0, 30287, 65085, 0, 'Говорящий череп to Джандис Барова');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 10, 0, 'Вот он! Убей его, $p!', 14, 0, 100, 1, 0, 29464, 65213, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 11, 0, 'Ну вот. Теперь постарайся дожить до конца.', 12, 0, 100, 1, 0, 29460, 65072, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 12, 0, 'Говорящий Череп колеблется, входить ли в зал, но затем решается.', 16, 0, 100, 0, 0, 0, 65078, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64563, 13, 0, 'Нет! Вернись, трус!', 14, 0, 100, 15, 0, 27485, 65203, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64575, 0, 0, 'Боюсь, нам придется здесь задержаться. Орда может защитить нас.', 12, 0, 100, 0, 0, 0, 65064, 0, 'Чань Хой-Сань');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64578, 0, 0, 'Может, вернемся на Кунь-Лай? Здесь так странно пахнет.', 12, 0, 100, 0, 0, 0, 65063, 0, 'Цзун Цзюнь-Кит');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64578, 1, 0, 'А когда мы вернемся домой?', 12, 0, 100, 0, 0, 0, 65061, 0, 'Цзун Цзюнь-Кит');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64578, 2, 0, 'Я хочу к своим игрушкам. Хочу домой.', 12, 0, 100, 0, 0, 0, 65062, 0, 'Цзун Цзюнь-Кит');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64579, 0, 0, 'Can we go see the serpents?', 12, 0, 100, 0, 0, 0, 65056, 0, 'Jong Wik-Wung');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64579, 1, 0, 'Может, посмотрим тауренов? Они похожи на яунголов.', 12, 0, 100, 0, 0, 0, 65058, 0, 'Цзун Вик-Вун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64579, 2, 0, 'Может, посмотрим женщину-нежить? Она чудная.', 12, 0, 100, 0, 0, 0, 65059, 0, 'Цзун Вик-Вун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64579, 3, 0, 'Может, поиграем с гоблинами? Смотри, я выше них!', 12, 0, 100, 0, 0, 0, 65060, 0, 'Цзун Вик-Вун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64579, 4, 0, 'Может, посмотрим змеев?', 12, 0, 100, 0, 0, 0, 65056, 0, 'Цзун Вик-Вун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64596, 0, 0, 'Мэр Хан, мы должны действовать решительно! Нужно хотя бы собрать стражников!', 12, 0, 100, 5, 0, 0, 67702, 0, 'Тэн Яблоневый Цвет to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 0, 0, 'Very good. Let`s talk.', 12, 0, 100, 1, 0, 29136, 65088, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 1, 0, 'Tong! Drinks, please.', 12, 0, 100, 1, 0, 29137, 65089, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 2, 0, 'My father, Deathwing, tried to destroy the whole of Azeroth.', 12, 0, 100, 1, 0, 29138, 65090, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 3, 0, 'He was misguided, of course, but he was right about one thing: Our world is... so fragile.', 12, 0, 100, 1, 0, 29139, 65091, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 4, 0, 'Wa are a point of light in a universe of shadow. A candle in a tempest.', 12, 0, 100, 1, 0, 29140, 65092, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 5, 0, 'Sometimes I think it was the very precariousness of our world that drove my father to madness.', 12, 0, 100, 1, 0, 29141, 65093, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 6, 0, 'Ah, thank you Tong.', 12, 0, 100, 1, 0, 29142, 65094, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 7, 0, 'Now to my point: I believe we are headed towards a reckoning. And no, I am not talking about the current conflict between the Aliance and the Horde.', 12, 0, 100, 1, 0, 29143, 65095, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 8, 0, 'Believe me - what Garrosh Hellscream achieved in Theramore is nothing compared to the horrors that are even now bearing down on our fragile home.', 12, 0, 100, 1, 0, 29144, 65096, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 9, 0, 'But the war deeply trubles me. Do you see my concern', 12, 0, 100, 1, 0, 29145, 65097, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 10, 0, 'A divided Azeroth cannot possibly stand against the darkness.', 12, 0, 100, 1, 0, 29146, 65098, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 11, 0, 'This war has to end. Soon. Before it consumes our Strength!', 12, 0, 100, 1, 0, 29147, 65099, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 12, 0, 'You can rest assured that my loyalties lie with Horde.', 12, 0, 100, 1, 0, 29149, 65101, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 13, 0, 'How do we bring a swift and decisive end to the conflict? I believe the answer lies with heroes like you.', 12, 0, 100, 1, 0, 29150, 65102, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 26, 0, 'You can rest assured that my loyalties lie with Aliance.', 12, 0, 100, 1, 0, 29148, 65100, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 28, 0, 'Tong! Something to eat, please.', 12, 0, 100, 1, 0, 34610, 70329, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 29, 0, 'Garrosh Hellscream! Warchief of the Horde.', 12, 0, 100, 1, 0, 34574, 70343, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 30, 0, 'He did not always command such power.', 12, 0, 100, 1, 0, 34575, 70344, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 31, 0, 'On Draenor, he was a miserable wretch. The actions of his father hung heavy on his heart.', 12, 0, 100, 1, 0, 34576, 70345, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 32, 0, 'I understand you, Garrosh. I know what it is like to bear the weight of your father\'s deeds.', 12, 0, 100, 1, 0, 34577, 70346, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 33, 0, 'A warrior like Garrosh comes to power with something to prove.', 12, 0, 100, 1, 0, 34578, 70347, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 34, 0, 'Your Horde finds itself at a turning point. This war will define you.', 12, 0, 100, 1, 0, 34579, 70348, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 35, 0, 'What IS the Horde?', 12, 0, 100, 1, 0, 34580, 70349, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 36, 0, 'Is it an instrument of vengeance, hellbent to carve out a future for its people?', 12, 0, 100, 1, 0, 34581, 70350, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 37, 0, 'Is it a refuge for those who need shelter, a commonwealth of the downtrodden, built for mutual support?', 12, 0, 100, 1, 0, 34582, 70351, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 38, 0, 'Or is it an alliance of convenience, a confederacy of unlikely partners free to pursue their own agendas?', 12, 0, 100, 1, 0, 34583, 70352, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 39, 0, 'I asked you: Who is the soul of the Horde?', 12, 0, 100, 1, 0, 34584, 70374, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 40, 0, 'We will find the answers here in Pandaria.', 12, 0, 100, 1, 0, 34585, 70354, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 41, 0, 'Show me what your Horde is made of, Champion!', 12, 0, 100, 1, 0, 34586, 70355, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 42, 0, 'Prove to me that your Horde is worthy of ruling over Azeroth.', 12, 0, 100, 1, 0, 34587, 70356, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 44, 0, 'The High King. Varian Wrynn - Leader of the Alliance.', 12, 0, 100, 1, 0, 34561, 70330, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 45, 0, 'Did you know, he was once a gladiator? Oh yes, I\'ve been doing my research.', 12, 0, 100, 1, 0, 34562, 70331, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 46, 0, 'Suffering from amnesia, he fought for his life in Horde arenas.', 12, 0, 100, 1, 0, 34563, 70332, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 47, 0, 'There, he earned the name "Lo\'Gosh," or "Ghost Wolf."', 12, 0, 100, 1, 0, 34564, 70333, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 48, 0, 'He IS a fighter. But... is he a leader?', 12, 0, 100, 1, 0, 34565, 70334, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 49, 0, 'Think about it! His enemies took advantage of my father\'s Cataclysm.', 12, 0, 100, 1, 0, 34566, 70335, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 50, 0, 'Horde soldiers thundered across the realm in the wake of the devastation.', 12, 0, 100, 1, 0, 34567, 70336, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 51, 0, 'Varian Wrynn didn\'t use the disaster to gain a military advantage.', 12, 0, 100, 1, 0, 34568, 70337, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 52, 0, 'Is this because he is weak...? Or because he is principled?', 12, 0, 100, 1, 0, 34569, 70338, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 53, 0, 'Here\'s the bigger question: Can principles win a war?', 12, 0, 100, 1, 0, 34570, 70339, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 54, 0, 'Make no mistake. Pandaria will challenge your King.', 12, 0, 100, 1, 0, 34571, 70340, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 55, 0, 'Let us see what he is truly made of.', 12, 0, 100, 1, 0, 34572, 70341, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 56, 0, 'Let us see if your Alliance has what it takes to rule Azeroth.', 12, 0, 100, 1, 0, 34573, 70342, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 57, 0, 'You have proven to be an exemplary hero. A champion for your cause.', 12, 0, 100, 1, 0, 34602, 70371, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 58, 0, 'Do you recall our earlier conversation?', 12, 0, 100, 1, 0, 34603, 70372, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 59, 0, 'We spoke of your Warchief. His inner conflicts and his outer conquests.', 12, 0, 100, 1, 0, 34604, 70373, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 61, 0, 'Your efforts on Padaria\'s southern shore and elsewhere have given me my answer.', 12, 0, 100, 1, 0, 34606, 70375, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 62, 0, 'YOU are the soul of the Horde.', 12, 0, 100, 1, 0, 34607, 70376, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 63, 0, 'Your actions in the coming weeks will come to define your people.', 12, 0, 100, 1, 0, 34608, 70377, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 64, 0, 'I want to make sure that you are properly equipped for the trials ahead.', 12, 0, 100, 1, 0, 34609, 70378, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 65, 0, 'Tong: fetch my things!', 12, 0, 100, 1, 0, 34597, 70366, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 66, 0, 'Your dedication to your cause deserves to be rewarded.', 12, 0, 100, 1, 0, 34598, 70367, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 67, 0, 'Like you, I continue to refine my skills here in Pandaria. I think you will appreciate the outcome.', 12, 0, 100, 1, 0, 34599, 70368, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 68, 0, 'Choose your reward!', 12, 0, 100, 1, 0, 34600, 70369, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 71, 0, 'We discussed the merits of your High King. I have been observing him closely.', 12, 0, 100, 1, 0, 34590, 70359, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 72, 0, 'It is true: he has the spirit of the wolf. And he fights like a wolf.', 12, 0, 100, 1, 0, 34591, 70360, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 73, 0, 'Having seen him in action, I now know what that means.', 12, 0, 100, 1, 0, 34592, 70361, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 74, 0, 'Wolves are pack animals.', 12, 0, 100, 1, 0, 34593, 70362, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 75, 0, 'Your King is calling his pack to him, assembling an Alliance in the truest sense of the word.', 12, 0, 100, 1, 0, 34594, 70363, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 76, 0, 'His journey is long. He has only taken the first steps.', 12, 0, 100, 1, 0, 34595, 70364, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64616, 77, 0, 'But with heroes like you in his pack, there is little your Alliance cannot accomplish.', 12, 0, 100, 1, 0, 34596, 70365, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64623, 0, 0, '|TInterface\\Icons\\ability_warlock_improvedsoulleech:20|tВы попали под действие заклинания |cFFFF0000|Hspell:126228|h[Покалеченная душа]|h|r! Убивать!', 42, 0, 100, 0, 0, 0, 65154, 0, 'Severed Soul to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64631, 0, 0, 'Почувствуй большое страшное вуду!', 12, 0, 100, 0, 0, 0, 10438, 0, 'Знахарь из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64631, 1, 0, 'Колдунство троллей – самое сильное колдунство!', 12, 0, 100, 0, 0, 0, 10437, 0, 'Знахарь из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64631, 2, 0, 'Я из тебя амулет сделаю!', 12, 0, 100, 0, 0, 0, 10435, 0, 'Знахарь из племени Гурубаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64648, 0, 0, 'Тварь покрыта маслом, подожги его!', 14, 0, 100, 0, 0, 0, 65346, 0, 'Капитан стражи Шадо-Пан to Кри\'чон');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64648, 1, 0, 'Лучники, в атаку! Пусть сгорит дотла!', 14, 0, 100, 0, 0, 0, 65347, 0, 'Капитан стражи Шадо-Пан to Кри\'чон');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64757, 0, 0, 'Есть, свет!', 14, 0, 100, 0, 0, 0, 65504, 0, 'Шонуф to Шонуф');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64764, 0, 0, 'There are a lot of corpses out there.  Nicely done, $p.', 12, 0, 100, 21, 0, 0, 65384, 0, 'Hooded Crusader to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64841, 0, 0, 'Готово. У тебя получилось, $p!', 12, 0, 100, 1, 0, 0, 65470, 0, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64841, 1, 0, 'Привет, меня зовут Лилиан. Не возражешь, если я возьму клинки?', 12, 0, 100, 477, 0, 0, 65472, 0, 'Лилиан Восс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64841, 2, 0, 'Она достает клинки из тела Вайтмейн, и они меняют форму.', 16, 0, 100, 477, 0, 0, 65567, 0, 'Лилиан Восс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64841, 3, 0, 'Теперь можно и раскрыть карты.', 12, 0, 100, 463, 0, 0, 65471, 0, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64841, 4, 0, 'Рыцарь в капюшоне смотрит вниз на пронзенное мечом тело верховного инквизитора.', 16, 0, 100, 0, 0, 0, 65473, 0, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 0, 0, 'Он открыл зонтик, воткнул в землю и сел в его прохладной тени.', 12, 0, 100, 6, 0, 0, 65613, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 1, 0, 'Он закрыл глаза, и стал единым целым с землей. А потом – знаете, что случилось потом?', 12, 0, 100, 2, 0, 0, 65614, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 2, 0, 'Его зонтик... пророс! У него выросли корни, он стал деревом, и на нем расцвели цветы!', 12, 0, 100, 5, 0, 0, 65615, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 3, 0, 'Да, это правда. Можете пойти в Лес Посохов и увидеть его собственными глазами.', 12, 0, 100, 1, 0, 0, 65616, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 4, 0, 'Окруженный посохами всех предков, что были до нас, посохами, выросшими в гигантские деревья.', 12, 0, 100, 274, 0, 0, 65617, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 5, 0, 'Это не грустная история! Сам Лю Лан говорил: "Не стоит сожалеть о жизни, прожитой не зря".', 12, 0, 100, 1, 0, 0, 65618, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 6, 0, 'А он, я думаю, прожил очень хорошую жизнь. Всем, что у нас есть, мы обязаны Лю Лану, первому пандарену-путешественнику.', 12, 0, 100, 273, 0, 0, 65619, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 7, 0, 'Мы рассказываем его историю, чтобы память о нем не истерлась никогда.', 12, 0, 100, 2, 0, 0, 65620, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 8, 0, 'Спасибо, Жуолинь! Это было прекрасно.', 12, 0, 100, 21, 0, 0, 65621, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 9, 0, 'Можешь спеть ее еще раз, для тех, кто только что пришел?', 12, 0, 100, 6, 0, 0, 65622, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 10, 0, 'Это песня о Лю Лане, первом пандарене-путешественнике.', 12, 0, 100, 1, 0, 0, 65583, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 11, 0, 'Песня написана на древнем наречии, языке императоров и ученых. Вряд ли сейчас кто-то говорит на нем.', 12, 0, 100, 6, 0, 0, 65584, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 12, 0, 'Она рассказывает о его приключениях.', 12, 0, 100, 273, 0, 0, 65585, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 13, 0, 'Храбрый Лю Лан оседлал морскую черепаху и отправился исследовать мир.', 12, 0, 100, 1, 0, 0, 65586, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64875, 14, 0, 'Кто-нибудь помнит, как звали черепаху? Инь?', 12, 0, 100, 25, 0, 0, 65587, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64885, 0, 0, 'Здравствуй, $n! Хранитель истории внизу как раз начинает урок, может, ты хочешь послушать?', 12, 0, 100, 3, 0, 0, 65573, 0, 'Хранитель истории Цзань');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (64916, 0, 0, '%s приходит в ярость, разлучаясь со своим призывателеи янтаря!', 41, 0, 100, 0, 0, 0, 65747, 0, 'Кор\'тик - страж роя');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65111, 0, 0, 'Артиллерия богомолов уже здесь! Торопитесь, герои!', 12, 0, 100, 397, 0, 0, 64838, 0, 'Защитник Змеиного Хребта to Крик\'тик-разведчик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65160, 0, 0, 'Wrathion\'s agents whisper quietly to one another.', 16, 0, 100, 0, 0, 0, 67584, 0, 'Blacktalon Watcher');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65160, 1, 0, 'Шпионы Гневиона перешептываются между собой.', 16, 0, 100, 0, 0, 0, 67584, 0, 'Страж Черного Когтя');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65173, 0, 0, '|TInterface\\Icons\\trade_archaeology_delicatemusicbox.blp:20|tИмператорский визирь Зор\'лок исполняет |cFFFF0000|Hspell:123791|h[песнь императрице]|h|r, наносящую урон всем игрокам до тех пор, пока они не вступят с ним в схватку!', 41, 0, 100, 0, 0, 0, 63775, 0, 'Эхо поглощения to Эхо поглощения');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65253, 0, 0, 'Hold off the Zan\'thik while Rik\'kal the Dissector gathers his strength.', 41, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65376, 0, 0, 'Конечно! Выбирать группу, например!', 12, 0, 100, 1, 0, 28050, 63211, 0, 'Хмелевар-предок to Хмелевар-предок');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65407, 0, 0, 'Приветствую вас, чужеземцы! Мастер Снежный Вихрь ждет вас в школе. Не забудьте оказать ему подобающее почтение.', 12, 0, 100, 2, 0, 0, 66449, 0, 'Новообращенный Шадо-Пан to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65450, 0, 0, '%s пробуждается ото сна, услышав звуки битвы!', 16, 0, 100, 0, 0, 0, 66497, 0, 'Древний страж to Древний страж');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65558, 0, 0, 'Давай возьмемся за это вместе!', 12, 0, 100, 5, 0, 27320, 60609, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65558, 1, 3, 'Еще одной мартышкой меньше. Неплохо!', 12, 0, 100, 5, 0, 27315, 60622, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65558, 1, 2, 'Эта обезьяна нарывается!', 12, 0, 100, 5, 0, 27317, 60621, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65558, 1, 0, 'Мне нравится твой стиль боя!', 12, 0, 100, 5, 0, 27322, 60610, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65558, 1, 1, 'Это все, на что ты способен?!', 12, 0, 100, 5, 0, 27316, 60620, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65558, 2, 0, 'Увидимся у причала!', 12, 0, 100, 0, 0, 27319, 60618, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65576, 0, 0, 'Боюсь, что нет, сэр.', 12, 0, 100, 0, 0, 0, 66730, 0, 'Филип Люк to Мерфи Злоторф');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65577, 0, 0, 'Лучше потанцуй со мной, милашка!', 12, 0, 100, 0, 0, 0, 66763, 0, 'Коллин Гилберт');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65578, 0, 0, 'Hey there beautiful, I\'d like to make a withdrawal.', 12, 0, 100, 0, 0, 0, 66789, 0, 'Vinnie Morrison');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65578, 1, 0, 'Thank you very, very much.', 12, 0, 100, 0, 0, 0, 66791, 0, 'Vinnie Morrison');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65578, 2, 0, 'Vielen, vielen Dank.', 12, 0, 100, 0, 0, 0, 66791, 0, 'Winnie Morrison');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65578, 3, 0, 'Спасибо огромное!', 12, 0, 100, 0, 0, 0, 68657, 0, 'Винни Моррисон');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65578, 4, 0, 'Привет, красавица! Хочу снять деньги со счета.', 12, 0, 100, 0, 0, 0, 66789, 0, 'Винни Моррисон');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65578, 5, 0, 'Hey, Schätzchen, ich würde gerne was abheben.', 12, 0, 100, 0, 0, 0, 66789, 0, 'Winnie Morrison');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65594, 0, 0, 'У тебя есть шипучие энергетические напитки?', 12, 0, 100, 6, 0, 0, 66755, 0, 'Твитс Легкозубчик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65594, 1, 0, 'Непросто быть легендарной исследовательницей!', 12, 0, 100, 396, 0, 0, 66771, 0, 'Твитс Легкозубчик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65598, 0, 0, 'Твоему народу суждено подчиниться.', 12, 0, 100, 0, 0, 0, 57194, 0, 'Красарангский хранитель рун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65598, 1, 0, 'Отведи взгляд, крестьянин!', 12, 0, 100, 0, 0, 0, 57193, 0, 'Красарангский хранитель рун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65598, 2, 0, 'Мы возродимся, и тогда вы будете служить нам!', 12, 0, 100, 0, 0, 0, 57190, 0, 'Красарангский хранитель рун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65598, 3, 0, 'Склонись пред нами!', 12, 0, 100, 0, 0, 0, 57195, 0, 'Красарангский хранитель рун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65616, 0, 0, 'A furious Orchard Needler appears above you!', 42, 0, 100, 0, 0, 0, 66906, 0, 'JustSummonedText');
