INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65626, 1, 0, 'Властелин Грома вернется, и тогда вам не устоять!', 12, 0, 100, 0, 0, 0, 57191, 0, 'Головорез из Доцзяна to Player');
