INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65626, 3, 0, 'Преклони колена, проситель!', 12, 0, 100, 0, 0, 0, 57189, 0, 'Головорез из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65635, 0, 0, 'Сапог... много не бывает!', 12, 0, 100, 1, 0, 0, 66872, 0, 'Темный дух - плут to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65635, 1, 0, 'Мне просто хотелось... еще... парочку...', 12, 0, 100, 1, 0, 0, 66871, 0, 'Темный дух - плут to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65638, 0, 0, 'Открывайте портал на вершину Кунь-Лай! Живее, идиоты!', 12, 0, 100, 5, 0, 0, 67046, 0, 'Надсмотрщик из Корцзя');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65638, 1, 0, 'Доставьте рабов на вершину Кунь-Лай! Открыть портал!', 12, 0, 100, 0, 0, 0, 67044, 0, 'Надсмотрщик из Корцзя');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65638, 2, 0, 'Закрой рот, раб! Мертвый ты будешь стоить столько же, сколько и живой.', 12, 0, 100, 0, 0, 0, 67047, 0, 'Надсмотрщик из Корцзя');
