INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 0, 0, 'Молодец, $p! Еле увернулись.', 14, 0, 100, 0, 0, 0, 71294, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 1, 0, 'Враг взял тебя в цель! Щит! Включай щит!', 14, 0, 100, 0, 0, 0, 70846, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 2, 0, 'Целься в янтарные бомбы! Они нанесут колоссальный урон, если их подорвать!', 14, 0, 100, 0, 0, 0, 70885, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 3, 0, 'Нужно их уничтожить до того, как нас вытеснят войска Орды.', 14, 0, 100, 0, 0, 0, 70894, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 4, 0, 'Используйте силовой щит, чтобы защититься от управляемых снарядов.', 42, 0, 100, 0, 0, 0, 70844, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 5, 0, 'Хорошо $gотстрелялся:отстрелялась,, $p! Щит ослабевает!', 14, 0, 100, 0, 0, 0, 70896, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 6, 0, 'Ты подходишь к первым башням! Уничтожь их!', 14, 0, 100, 0, 0, 0, 70895, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 7, 0, 'Гладко сработано, $p! Продолжай сбрасывать бомбы.', 14, 0, 100, 0, 0, 0, 71290, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 8, 0, 'Те богомолы защищают башни, $p. Нужно их ликвидировать, иначе мы не сможем вести обстрел.', 14, 0, 100, 0, 0, 0, 71280, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 9, 0, 'По донесениям разведки этот щит поддерживает несколько генераторов, расположенных по всему острову.', 14, 0, 100, 0, 0, 0, 70893, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 10, 0, 'Остров охвачен звуковым щитом, $p.', 14, 0, 100, 0, 0, 0, 70892, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 11, 0, 'Щит ликвидирован! Быстрее отправляйся на береговой плацдарм!', 14, 0, 100, 0, 0, 0, 70898, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 12, 0, 'Передаю координаты следующих башен.', 14, 0, 100, 0, 0, 0, 70897, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 13, 0, 'Прием, $p, это небесный адмирал Роджерс!', 14, 0, 100, 0, 0, 0, 71141, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68860, 14, 0, 'Скорпиды перемещают башни! Уничтожь их!', 14, 0, 100, 0, 0, 0, 71281, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68930, 0, 0, 'Что?..', 12, 0, 100, 6, 0, 0, 69733, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68930, 1, 0, 'Андуин! Ты жив!', 12, 0, 100, 5, 0, 0, 69735, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68930, 2, 0, 'Значит, мы защитим их – даже ценой наших жизней. Андуин, вместе мы сможем сделать это.', 12, 0, 100, 1, 0, 0, 69739, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68930, 3, 0, 'Маршал, вы отлично укрепили бухту. Дальше займемся...', 12, 0, 100, 1, 0, 0, 69731, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68930, 4, 0, 'Тихо, тихо. Здесь мы ведем иную войну. Мы воюем не из ненависти к Орде. Мы просто пытаемся сделать то, что должны.', 12, 0, 100, 0, 0, 0, 69737, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68930, 5, 0, 'Следопыты?.. Тиранда.', 12, 0, 100, 1, 0, 0, 69742, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68944, 0, 0, 'Отец, нет! Ненависть и злоба, порождаемые нашей войной, отравляют это место.', 12, 0, 100, 274, 0, 0, 69736, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68944, 1, 0, 'Эти земли другие. Они просто сокровище.', 12, 0, 100, 1, 0, 0, 69738, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68944, 2, 0, 'Отец!', 14, 0, 100, 22, 0, 0, 124735, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68944, 3, 0, 'Вместе...', 12, 0, 100, 1, 0, 0, 114837, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68945, 0, 0, 'Ваше величество! Орда взяла храм к западу отсюда. Следопыты эльфов окружили его и никого не выпускают живым...', 14, 0, 100, 5, 0, 0, 69741, 0, 'Хильда Хорнсвоггл to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68963, 0, 0, 'Благодаря |3-2($n) бумзал потасовочной "У Бизмо" распахнул свои двери для всех бойцов!', 14, 0, 100, 0, 0, 80244, 127276, 0, 'Вышибала бумзала to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68970, 0, 0, 'Эта резня любого заставит подумать дважды о вступлении в Орду.', 12, 0, 100, 274, 0, 0, 69602, 0, 'Вол\'джин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68970, 1, 0, 'Я пойду, но только как свидетель от лица своего народа.', 12, 0, 100, 1, 0, 0, 71275, 0, 'Вол\'джин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68970, 2, 0, 'Вопрос в том, что МЫ здесь делаем. Ты несешь войну на очередной материк, и я хочу знать, зачем.', 12, 0, 100, 6, 0, 0, 69599, 0, 'Вол\'джин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68975, 0, 0, 'Ты можешь кое-что сделать, чтобы доказать свою ценность для Орды, тролль. Я дам тебе задание в самом сердце этих земель.', 12, 0, 100, 1, 0, 0, 69604, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68975, 1, 0, '%s и Рак\'гор Кровавая Бритва шепотом переговариваются между собой.', 16, 0, 100, 1, 0, 0, 71278, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68975, 2, 0, 'Тебе не хватает проницательности, дикарь. А еще тебе не хватает верности – потому я и не позвал с собой троллей Черного Копья.', 12, 0, 100, 1, 0, 0, 69598, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68975, 3, 0, 'Посмотри вокруг, Вол\'джин. Здесь есть все, что нам нужно: древесина, камень, руда, нефть. Воины.', 12, 0, 100, 1, 0, 0, 69600, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68975, 4, 0, 'Демонстрация силы на этих берегах заставит пандаренов подумать дважды о вступлении в Альянс.', 12, 0, 100, 5, 0, 0, 69601, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68975, 5, 0, 'Разница между мной и тобой, Вол\'джин, состоит в том, что я не даю СВОИМ умирать от голода в пустыне. Меня ничто, НИЧТО не остановит на пути к славному будущему для орков и всех, у кого хватит смелости пойти с нами. Жди здесь.', 12, 0, 100, 25, 0, 0, 69603, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68975, 6, 0, 'Вол\'джин. Что ты здесь делаешь? Не сидится на своем острове в компании жаб?', 12, 0, 100, 547, 0, 0, 69597, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (68975, 7, 0, 'Видишь, герой? Моя Орда может добиться чего угодно. Это еще что?', 12, 0, 100, 1, 0, 0, 69596, 0, 'Гаррош Адский Крик to Player');
