INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70023, 1, 0, 'Надо спрыгнуть со стены Черного Храма и посмотреть, что там внизу.', 12, 0, 100, 0, 0, 0, 72267, 0, 'Надоедливый бес to Надоедливый бес');
