INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70794, 2, 0, 'Что это за ощущение? Это... свет?..', 14, 0, 100, 18, 0, 0, 74168, 0, 'Слепой герой to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70966, 0, 0, 'Привет! Я гидролизатор модели два-дефис-ноль, иначе – Н2-0.', 12, 0, 100, 0, 0, 0, 73425, 0, 'H2-0 to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70966, 1, 0, 'I\'ve been sent here to help you keep Anthracite under control.', 12, 0, 100, 0, 0, 0, 73426, 0, 'H2-0 to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70966, 2, 0, 'Я снабжен дополнительным резервуаром воды, но тебе придется добыть больше жидкости, если захочешь провести гидролиз несколько раз.', 15, 0, 100, 0, 0, 0, 73427, 0, 'H2-0 to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70966, 3, 0, 'Hey there! I\'m a Hydrolizer model two, dash zero or, H2-0 for short.', 12, 0, 100, 0, 0, 0, 73425, 0, 'H2-0 to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70966, 4, 0, 'Я здесь, чтобы помочь тебе удерживать Антрацита под контролем.', 12, 0, 100, 0, 0, 0, 73426, 0, 'H2-0 to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70983, 0, 0, 'Замечена враждебная цель.', 14, 0, 100, 0, 0, 0, 73121, 0, 'Двухфазный робот Т440');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70983, 1, 0, 'Вертикальные стабилизаторы неактивны. Питание отключено.', 14, 0, 100, 0, 0, 34758, 73122, 0, 'Двухфазный робот Т440 to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70983, 2, 0, 'Цель недоступна. Завершение боевых действий.', 14, 0, 100, 0, 0, 34755, 73123, 0, 'Двухфазный робот Т440');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70984, 0, 0, 'Вол\'джин жив!', 14, 0, 100, 22, 0, 0, 73984, 0, 'Верный служитель Вол\'джина');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 0, 0, 'Поняв это, поймешь и суть мудрости.', 12, 0, 100, 2, 0, 0, 73752, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 1, 0, 'Мудр тот, кто знает, что правильно. А иногда правильно бездействовать.', 12, 0, 100, 2, 0, 0, 73751, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 2, 0, 'Я хочу проверить, способны ли вы проявить смекалку и ловкость в миг смертельной опасности.', 12, 0, 100, 2, 0, 0, 73754, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 3, 0, 'Слова истинного потомка Аспектов, вечно стремящихся изменить мир.', 12, 0, 100, 2, 0, 0, 73750, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 4, 0, 'Подойди, и я благословлю тебя.', 12, 0, 100, 2, 0, 0, 73753, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 5, 0, 'Spoken like a true child of the Aspects. Always seeking to change the world.', 12, 0, 100, 2, 0, 0, 73750, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 6, 0, 'Видишь? Слепая сила несет лишь хаос и разрушение.', 12, 0, 100, 2, 0, 0, 73783, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 7, 0, 'Ah, the dragon child and the hero. Together you will achieve great things.', 12, 0, 100, 2, 0, 0, 73748, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 8, 0, 'Прошу тебя, юный принц, запомни этот урок.', 12, 0, 100, 2, 0, 0, 73882, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 9, 0, 'Скажи мне, юный дракон: в чем суть мудрости?', 12, 0, 100, 2, 0, 0, 73749, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 10, 0, 'Do you see? Power without vision causes only random chaos and destruction.', 12, 0, 100, 2, 0, 0, 73783, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 11, 0, 'Tell me, dragon child: What is the nature of wisdom?', 12, 0, 100, 2, 0, 0, 73749, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70989, 12, 0, 'А, юный дракон и герой. Вместе вы совершите великие дела.', 12, 0, 100, 2, 0, 0, 73748, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71002, 0, 0, 'Никто не сможет нам противостоять!', 12, 0, 100, 0, 0, 0, 74218, 0, 'Кор\'кронский поджигатель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71003, 0, 0, 'Делать то, что правильно. Любой ценой.', 12, 0, 100, 1, 0, 0, 73746, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71003, 1, 0, 'Когда же нужно бездействовать ради блага?', 12, 0, 100, 1, 0, 0, 73747, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71003, 2, 0, 'To do what is right, no matter the cost. That is wisdom.', 12, 0, 100, 1, 0, 0, 73746, 0, 'Wrathion to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71007, 0, 0, 'Никто не сможет нам противостоять!', 12, 0, 100, 0, 0, 0, 74218, 0, 'Кор\'кронский воспламенитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71007, 1, 0, 'Победа или смерть!', 12, 0, 100, 0, 0, 0, 90976, 0, 'Кор\'кронский воспламенитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71015, 0, 0, 'Вход только по приглашениям.', 12, 0, 100, 0, 0, 37518, 74120, 0, 'Смотритель зверинца');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71015, 1, 0, 'Экспонаты руками не трогать.', 12, 0, 100, 0, 0, 37517, 74121, 0, 'Смотритель зверинца');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71015, 2, 0, 'У вас нет приглашения.', 12, 0, 100, 0, 0, 28382, 74122, 0, 'Смотритель зверинца');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71033, 0, 0, 'Эй, а ты ведь не тот вонючий баран. Чего тебе надо от Кил\'река?', 12, 0, 100, 0, 0, 0, 74119, 0, 'Злобный бес to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 0, 0, 'А-ха!', 12, 0, 100, 0, 0, 0, 74026, 0, 'Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 1, 0, 'Гневион ничего не видит, нападайте сзади, чтобы нанести дополнительный урон!', 41, 0, 100, 0, 0, 0, 74011, 0, 'Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 2, 0, 'Но... это абсурд! Я убью своего же избранника!', 12, 0, 100, 274, 0, 0, 73512, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 3, 0, 'Интересно... Хотя исход вряд ли изменится.', 12, 0, 100, 6, 0, 0, 73513, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 4, 0, 'Слепой дракон. Какая нелепица!', 12, 0, 100, 1, 0, 0, 73517, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 5, 0, 'Хорошо. Прости, герой, но сегодня не мне предстоит усвоить урок.', 12, 0, 100, 1, 0, 0, 73514, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 6, 0, '%s ворчит себе под нос, затягивая повязку на глазах.', 16, 0, 100, 1, 0, 0, 73516, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 7, 0, 'Как я и думал. Попробуем еще раз?', 12, 0, 100, 6, 0, 0, 73778, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 8, 0, 'Стой! Я же должен тебя убить!', 12, 0, 100, 0, 0, 0, 74025, 0, 'Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 9, 0, 'Найдешь ли ты меня теперь?', 14, 0, 100, 0, 0, 0, 73469, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 10, 0, 'Глаза подводят тебя!', 14, 0, 100, 0, 0, 0, 73466, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 11, 0, 'Куда же ты? Вернись.', 12, 0, 100, 0, 0, 0, 74027, 0, 'Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71037, 12, 0, 'Неплохо!', 14, 0, 100, 0, 0, 0, 117372, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 0, 0, 'Тогда начнем! Герой, тебе придется одолеть Черного принца в ближнем бою.', 12, 0, 100, 1, 0, 0, 73508, 0, 'Сюэнь to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 1, 0, 'Держись позади него. Он не видит тебя! Двигайся быстрее!', 14, 0, 100, 1, 0, 0, 73523, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 2, 0, 'Тебе, Гневион, я завяжу глаза.', 12, 0, 100, 1, 0, 0, 73509, 0, 'Сюэнь to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 3, 0, 'Ищи истинного врага! Не дай фальшивым образам обмануть тебя!', 14, 0, 100, 1, 0, 0, 73880, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 4, 0, 'Отлично, сложите оружие!', 14, 0, 100, 1, 0, 0, 73657, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 5, 0, 'Уклоняйся, используй его силу против него!', 14, 0, 100, 1, 0, 0, 73538, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 6, 0, 'Дракон сильнее тебя, герой! Не дай ему завершить атаку!', 14, 0, 100, 1, 0, 0, 73519, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 7, 0, 'Ты очень силен. Но сила бесполезна, если ты не видишь, куда ее направить.', 12, 0, 100, 1, 0, 0, 73510, 0, 'Сюэнь to Сюэнь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 8, 0, 'Довольно!', 14, 0, 100, 1, 0, 0, 129681, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 9, 0, 'Вперед!', 14, 0, 100, 1, 0, 0, 131943, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 10, 0, 'Они бессмертны! Действуй быстрее, пока они снова не напали на тебя!', 14, 0, 100, 1, 0, 0, 73881, 0, 'Сюэнь to Слеза Нелтариона');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71136, 11, 0, 'Герой, обрати силу соперника против него самого. Я верю в тебя.', 12, 0, 100, 1, 0, 0, 73664, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 0, 0, 'Не отвлекайся на них!', 14, 0, 100, 2, 0, 0, 73772, 0, 'Юй-лун to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 1, 0, 'Довольно!', 14, 0, 100, 2, 0, 0, 129681, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 2, 0, 'You, my dark prince, will be blindfolded.', 12, 0, 100, 2, 0, 0, 73766, 0, 'Yu\'lon to Yu\'lon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 3, 0, 'Be aware of your surroundings.', 14, 0, 100, 2, 0, 0, 73771, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 4, 0, 'Seek out your true enemy! Do not be fooled by his false images!', 14, 0, 100, 2, 0, 0, 73880, 0, 'Yu\'lon to Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 5, 0, 'We will demonstrate the limits of power without vision to guide it.', 12, 0, 100, 2, 0, 0, 73767, 0, 'Yu\'lon to Yu\'lon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 6, 0, 'Very good - that is enough!', 14, 0, 100, 2, 0, 0, 73781, 0, 'Yu\'lon to Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 7, 0, 'Смотри по сторонам.', 14, 0, 100, 2, 0, 0, 73771, 0, 'Юй-лун to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 8, 0, 'Отлично, хватит!', 14, 0, 100, 2, 0, 0, 73781, 0, 'Юй-лун to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 9, 0, 'The elements are undying! Make use of your time before they rise again!', 14, 0, 100, 1, 0, 0, 73881, 0, 'Yu\'lon to Neltharion\'s Tears');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 10, 0, 'Let the challenge begin! Hero, you must defeat the Black Prince in combat.', 12, 0, 100, 2, 0, 0, 73765, 0, 'Yu\'lon to Yu\'lon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 11, 0, 'Они бессмертны! Действуй быстрее, пока они снова не напали на тебя!', 14, 0, 100, 1, 0, 0, 73881, 0, 'Юй-лун to Слеза Нелтариона');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71270, 12, 0, 'Do not allow the elements to engage you!', 14, 0, 100, 2, 0, 0, 73772, 0, 'Yu\'lon to Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 0, 0, 'A blind dragon. Ridiculous!', 12, 0, 100, 1, 0, 0, 73517, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 1, 0, '... interesting. But the outcome is unlikely to change.', 12, 0, 100, 6, 0, 0, 73513, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 2, 0, 'Гневион ничего не видит, нападайте сзади, чтобы нанести дополнительный урон!', 41, 0, 100, 0, 0, 0, 74011, 0, 'Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 3, 0, 'Wrathion is blindfolded, so attack from behind for bonus damage!', 41, 0, 100, 0, 0, 0, 74011, 0, 'Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 4, 0, 'Wait - that\'s absurd. I would annihilate my own champion!', 12, 0, 100, 274, 0, 0, 73512, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 5, 0, '%s ворчит себе под нос, затягивая повязку на глазах.', 16, 0, 100, 0, 0, 0, 73516, 0, 'Гневион to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 6, 0, '%s mutters to himself as he ties the blindfold.', 16, 0, 100, 1, 0, 0, 73516, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 7, 0, 'Very well. I am sorry, champion - But I will not be the one learning a lesson today.', 12, 0, 100, 1, 0, 0, 73514, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 8, 0, 'А-ха!', 12, 0, 100, 0, 0, 0, 74026, 0, 'Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 9, 0, 'Where did you go? Come back!', 12, 0, 100, 0, 0, 0, 74027, 0, 'Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 10, 0, 'Стой! Я же должен тебя убить!', 12, 0, 100, 0, 0, 0, 74025, 0, 'Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 11, 0, 'Куда же ты? Вернись.', 12, 0, 100, 0, 0, 0, 74027, 0, 'Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 12, 0, 'Ahhh... Hah!', 12, 0, 100, 0, 0, 0, 73776, 0, 'Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 13, 0, 'Your eyes fail you yet!', 14, 0, 100, 0, 0, 0, 73466, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 14, 0, 'But can you find me again?', 14, 0, 100, 0, 0, 0, 73469, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 15, 0, 'Well done!', 14, 0, 100, 0, 0, 0, 117372, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71279, 16, 0, 'Stand still! I am trying to kill you here!', 12, 0, 100, 0, 0, 0, 74025, 0, 'Wrathion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71280, 0, 0, '|TInterface\\Icons\\inv_misc_pocketwatch_01:20|t Начался дополнительный отcчет времени!', 41, 0, 100, 0, 0, 0, 74194, 0, 'Controller to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 0, 0, 'Молодец! Ты справился!', 14, 0, 100, 1, 0, 0, 73830, 0, 'Чи-Цзи to Видение Смертокрыла');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 1, 0, 'Но не впадай в отчаяние – твой дух сильнее, чем ты думаешь. Попробуй еще раз, я верю в тебя!', 12, 0, 100, 1, 0, 0, 73806, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 2, 0, 'Не упускай возможности исцелить Черного принца. ', 14, 0, 100, 1, 0, 0, 73794, 0, 'Чи-Цзи to Видение Смертокрыла');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 3, 0, 'Хорошо. Испытание окончено.', 14, 0, 100, 1, 0, 0, 73805, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 4, 0, 'Не страшись его ловушек. Твое мастерство лекаря сильнее его магии!', 14, 0, 100, 1, 0, 0, 73799, 0, 'Чи-Цзи to Видение Смертокрыла');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 5, 0, 'Начнем! Герой, используй свой дар лекаря, чтобы сохранить жизнь Черному принцу.', 12, 0, 100, 1, 0, 0, 73785, 0, 'Чи-Цзи to Чи-Цзи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 6, 0, 'Исцеляй принца, но не забывай и о себе!', 14, 0, 100, 1, 0, 0, 73793, 0, 'Чи-Цзи to Видение Смертокрыла');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 7, 0, 'Твой герой поможет тебе. Вперед!', 14, 0, 100, 1, 0, 0, 73788, 0, 'Чи-Цзи to Чи-Цзи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 8, 0, 'Ты еще не примирился со своим прошлым. Сделай это сейчас.', 12, 0, 100, 1, 0, 0, 73787, 0, 'Чи-Цзи to Чи-Цзи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 9, 0, 'Они бессмертны! Действуй быстрее, пока они снова не напали на тебя!', 14, 0, 100, 1, 0, 0, 73881, 0, 'Чи-Цзи to Слеза Нелтариона');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71314, 10, 0, 'Разве? Ты полон страхов и сомнений, юный принц.', 12, 0, 100, 1, 0, 0, 73786, 0, 'Чи-Цзи to Чи-Цзи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71317, 0, 0, 'Славная работа, герой! Мы прошли испытание Небожителей и получили их благословение.', 12, 0, 100, 5, 0, 37430, 73916, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71317, 1, 0, 'Теперь возвращайся на Причуду Камнетеса – я подготовил для тебя приятный сюрприз!', 12, 0, 100, 5, 0, 37431, 73917, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71317, 2, 0, 'Ха! Это был нечестный бой.', 12, 0, 100, 1, 0, 0, 73782, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71317, 3, 0, 'Я рад, что наш герой все еще жив. Могучий тигр, спасибо за благословение.', 12, 0, 100, 0, 0, 0, 73661, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71357, 0, 0, 'Это... это... мой отец?', 12, 0, 100, 1, 0, 0, 73817, 0, 'Гневион to Чи-Цзи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71357, 1, 0, 'Прошу... я не хочу этого.', 12, 0, 100, 274, 0, 0, 73818, 0, 'Гневион to Чи-Цзи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71357, 2, 0, 'Я черный дракон. Мне не нужна помощь.', 12, 0, 100, 1, 0, 0, 73816, 0, 'Гневион to Чи-Цзи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71357, 3, 0, 'Гневион побежден и без сил падает на землю!', 41, 0, 100, 1, 0, 0, 73970, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71357, 4, 0, 'Хватит! Довольно!', 14, 0, 100, 1, 0, 0, 73804, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71363, 0, 0, 'Ваши усилия ничего не изменят!', 14, 0, 100, 0, 0, 0, 73801, 0, 'Видение Смертокрыла');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71363, 1, 0, 'Я разорву этот мир на части!', 14, 0, 100, 0, 0, 0, 73792, 0, 'Видение Смертокрыла to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71363, 2, 0, 'Видение Смертокрыла нацелилось на Гневиона! Его необходимо срочно исцелить!', 41, 0, 100, 0, 0, 0, 0, 0, 'Видение Смертокрыла');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (71363, 3, 0, 'Защитите Гневиона от атак, прикрывая его своим телом.', 41, 0, 100, 1, 0, 0, 73930, 0, 'Видение Смертокрыла');
