INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72640, 0, 0, 'Шаман клана Громового Молота ставит тотем сопротивления, чтобы помочь вам!', 41, 0, 100, 0, 0, 0, 74895, 0, 'Шаман клана Громового Молота to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72654, 0, 0, 'Голоса... смолкли. Спасибо вам, друзья мои.', 12, 0, 100, 1, 0, 93883, 140492, 0, 'Духостранник Смоляной Рог to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72695, 0, 0, 'Немногие смертные так сильны духом и телом. Поверь, я испытывал многих!', 12, 0, 100, 1, 0, 0, 75465, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72695, 1, 0, 'Отправляйся в Вечноцветущий дол. Мы встретимся с тобой в Обители Мудрости. ', 12, 0, 100, 1, 0, 0, 75468, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72695, 2, 0, 'Великое дело можно завершить лишь в священном месте.', 12, 0, 100, 1, 0, 0, 75467, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72695, 3, 0, 'Победа за тобой…', 12, 0, 100, 1, 0, 0, 75463, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72723, 0, 0, 'Встретимся на Сокрытой лестнице, когда довершишь свое дело. Если останешься в живых.', 12, 0, 100, 1, 0, 0, 76155, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72723, 1, 0, 'О! А вот и наш герой!', 12, 0, 100, 0, 0, 0, 75045, 0, 'Гневион to Player');
