INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (72770, 9, 0, 'Когда я усовершенствую процесс приготовления, сила И\'Шараджа сделает нас непобедимыми!', 14, 0, 100, 22, 0, 0, 75801, 0, 'Кор\'кронский темный прорицатель to Player');
