INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73166, 0, 0, 'Хребтохват убегает в страхе!', 16, 0, 100, 0, 0, 0, 76069, 0, 'Огромный хребтохват');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73195, 0, 0, 'Сначала – дьявозавр, остальные – потом.', 14, 0, 100, 0, 0, 0, 75724, 0, 'Кор\'кронский тюремщик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73195, 1, 0, 'Бейте его током, пока не свалится! Он сеет хаос.', 14, 0, 100, 0, 0, 0, 75723, 0, 'Кор\'кронский тюремщик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73195, 2, 0, 'Он вырывается из клетки. Включить шоковый ошейник, усмирить зверя!', 14, 0, 100, 0, 0, 0, 75722, 0, 'Кор\'кронский тюремщик to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73195, 3, 0, 'Shock it until it can\'t move anymore!  It\'s causing a riot in here!', 14, 0, 100, 5, 0, 0, 75723, 0, 'Kor\'kron Jailer to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73195, 4, 0, 'It\'s busting out of the cage!  Arm the shock collar, get that thing under control!', 14, 0, 100, 5, 0, 0, 75722, 0, 'Kor\'kron Jailer to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73195, 5, 0, 'Deal with the devilsaur first, then we\'ll lock the rest up!', 14, 0, 100, 5, 0, 0, 75724, 0, 'Kor\'kron Jailer to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73222, 0, 0, 'A soft hum ceases as the corruption creating new Puddles ebbs away with the death of the final Aqueous Defender.', 16, 0, 100, 0, 0, 0, 77144, 0, 'Ooze Controller (Stalker) to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73222, 1, 0, 'Тихое жужжание замирает по мере того, как скверна исчезает после смерти последнего водяного защитника.', 16, 0, 100, 0, 0, 0, 77144, 0, 'Ooze Controller (Stalker) to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 0, 0, 'Let the intruders come this way! I look forward to crushing their skulls.', 14, 0, 100, 0, 0, 0, 75806, 0, 'Kor\'kron Wild Gun to Kor\'kron Iron Scorpion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 1, 0, 'Где эти чужаки? Пусть приходят! Очень уж хочется проломить пару голов.', 14, 0, 100, 0, 0, 0, 75806, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский железный скорпион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 2, 0, 'Of course, those cowards will likely sneak by rather than face my wrath.', 14, 0, 100, 0, 0, 0, 75807, 0, 'Kor\'kron Wild Gun to Kor\'kron Iron Scorpion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 3, 0, 'Но, конечно, эти трусы скорее попрячутся в норы, чем решатся узреть мой гнев.', 14, 0, 100, 0, 0, 0, 75807, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский железный скорпион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 4, 0, 'Сейчас вы умрете, трусы! Вы откроете эти врата только через мой труп!', 14, 0, 100, 0, 0, 0, 76089, 0, 'Кор\'кронский дикий стрелок to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 5, 0, 'What else would you expect from those without valor?', 14, 0, 100, 0, 0, 0, 75808, 0, 'Kor\'kron Wild Gun to Kor\'kron Iron Scorpion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 6, 0, 'I will burn your corpses to ash!', 12, 0, 100, 0, 0, 0, 76076, 0, 'Kor\'kron Wild Gun to Kor\'kron Wild Gun');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 7, 0, 'Я превращу твои кости в прах!', 12, 0, 100, 0, 0, 0, 76076, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский дикий стрелок');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 8, 0, 'Falling to me would probably be the greatest achievement they could hope to obtain.', 14, 0, 100, 0, 0, 0, 75809, 0, 'Kor\'kron Wild Gun to Kor\'kron Iron Scorpion');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 9, 0, 'Смерть от моей руки была бы их величайшим достижением!', 14, 0, 100, 0, 0, 0, 75809, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский железный скорпион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73250, 10, 0, 'Чего еще ожидать от бесчестных шавок?', 14, 0, 100, 0, 0, 0, 75808, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский железный скорпион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73278, 0, 0, '|TInterface\\Icons\\ability_garrosh_siege_engine.blp:20|t Капитан обнаружил скопление сил противника и запускает в него |cFFFF0000|Hspell:147047|h[нестабильную железную звезду]|h|r!', 41, 0, 100, 0, 0, 0, 75799, 0, 'Clump to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73318, 0, 0, 'Сюда, герой!', 12, 0, 100, 0, 0, 39072, 75826, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73318, 1, 0, 'Наконец-то тьма, которую оно источало, побеждена.', 12, 0, 100, 378, 0, 39074, 75828, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73318, 2, 0, 'Идем, герой. Вернемся в дол. Я думаю, кое-кому будет особенно интересно узнать о таком повороте событий. Если только мы сможем привлечь его внимание.', 12, 0, 100, 0, 0, 39075, 75829, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73318, 3, 0, 'Смотри, что осталось от чудовища, которое угнетало Пандарию все эти годы.', 12, 0, 100, 3, 0, 39073, 75827, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 0, 0, 'Как он допустил появление нового вождя? Он мог объединить мир под стягом Альянса. И почему я верил в него!', 12, 0, 100, 6, 0, 0, 75952, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 1, 0, 'Ну конечно, еще год войны. Огромные потери при штурме Громового Утеса. Остальная Орда развалилась бы.', 12, 0, 100, 1, 0, 0, 75838, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 2, 0, 'Обещаю: я не остановлюсь ни перед чем, чтобы подготовить мир к предстоящей битве.', 12, 0, 100, 1, 0, 0, 75987, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 3, 0, 'Я считал, Адский Крик победит, но он восстановил против себя половину Орды. И я передумал.', 12, 0, 100, 1, 0, 0, 75841, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 4, 0, 'Что? Да, я поддерживал Альянс.', 12, 0, 100, 273, 0, 0, 75840, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 5, 0, 'Э…?', 12, 0, 100, 6, 0, 0, 75985, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 6, 0, 'Король — глупец!', 12, 0, 100, 5, 0, 0, 75836, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 7, 0, 'Надо было самому занять трон. Как тетушка Ониксия. Я бы навел порядок!', 12, 0, 100, 1, 0, 0, 75953, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 8, 0, 'ТУН! ПИТЬ! ЖИВО!', 12, 0, 100, 25, 0, 0, 75839, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 9, 0, 'Я сделал все, что мог. Весь мир лежал у его ног, нужно было лишь взять его!', 12, 0, 100, 5, 0, 0, 75837, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 10, 0, 'Неужели в Оргриммаре все произошло так, как ты говоришь?', 12, 0, 100, 25, 0, 0, 75834, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 11, 0, 'Ни в чем!', 12, 0, 100, 5, 0, 0, 75989, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 12, 0, 'Больше никаких случайностей!', 12, 0, 100, 1, 0, 0, 75988, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 13, 0, 'Но этот идиот Ринн! "Его Величество"!', 12, 0, 100, 5, 0, 0, 75951, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 14, 0, '...ты …ты просто трактирщик!', 12, 0, 100, 25, 0, 0, 75986, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73319, 15, 0, 'О, не удивляйся так сильно. Я – черный дракон. Я верен только себе.', 12, 0, 100, 274, 0, 0, 75944, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 0, 0, 'Одни разговоры!', 12, 0, 100, 5, 0, 0, 75978, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 1, 0, 'Ты принимаешь вашу великую силу за слабость.', 12, 0, 100, 25, 0, 0, 75983, 0, 'Тун Устроитель to Тун Устроитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 2, 0, 'Хватит!', 12, 0, 100, 274, 0, 0, 125689, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 3, 0, 'Всегда говоришь. И никогда не слушаешь! Не видишь, чему учит Пандария! ', 12, 0, 100, 25, 0, 0, 75979, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 4, 0, 'Так дело обстоит и у Орды с Альянсом. Две стороны сильны не вопреки друг другу. Они сильны благодаря друг другу!', 12, 0, 100, 25, 0, 0, 75982, 0, 'Тун Устроитель to Тун Устроитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 5, 0, 'Природа стремится к равновесию. Даже в нашем мехе есть черное и белое. Тьма и свет.', 12, 0, 100, 1, 0, 0, 75980, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 6, 0, 'Он хотел сохранить целостность. Прожив рядом с богомолами десять тысяч лет, мы стали сильнее.', 12, 0, 100, 1, 0, 0, 75990, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 7, 0, 'Когда последний император укрыл Пандарию от остального мира, он уберег и землю наших древних врагов – богомолов. А почему?', 12, 0, 100, 1, 0, 0, 75981, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73346, 8, 0, '...Разрушил таверну и не дал на чай. Неприятный тип.', 12, 0, 100, 274, 0, 0, 75984, 0, 'Тун Устроитель to Тун Устроитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73493, 0, 0, 'Синий провод c зеленой полоской или черный провод с желтой? Вечно я их путаю!', 12, 0, 100, 0, 0, 0, 76168, 0, 'Специалист-взрывотехник to Помощник специалиста-взрывотехника');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73494, 0, 0, 'Подожди!', 12, 0, 100, 4, 0, 0, 76171, 0, 'Помощник специалиста-взрывотехника to Помощник специалиста-взрывотехника');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73495, 0, 0, 'Синий провод c зеленой полоской или черный провод с желтой? Вечно я их путаю!', 12, 0, 100, 0, 0, 0, 76168, 0, 'Специалист-взрывотехник to Помощник специалиста-взрывотехника');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73553, 0, 0, 'Wait for me!', 12, 0, 100, 4, 0, 0, 112949, 0, 'Demolitions Assistant to Demolitions Assistant');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73553, 1, 0, 'Подожди!', 12, 0, 100, 4, 0, 38152, 76171, 0, 'Помощник специалиста-взрывотехника to Помощник специалиста-взрывотехника');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73554, 0, 0, 'Вы все еще живы! Хорошо. На улицах хаос.', 14, 0, 100, 1, 0, 38081, 75219, 0, 'Леди Джайна Праудмур');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73554, 1, 0, 'Кор\'кронцы не сдаются! Они атакуют это укрепление!', 14, 0, 100, 5, 0, 38082, 75220, 0, 'Леди Джайна Праудмур');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73558, 0, 0, 'Повстанцы Вол\'джина несут потери, но наши ряды держатся.', 14, 0, 100, 1, 0, 38949, 75238, 0, 'Король Вариан Ринн to Леди Джайна Праудмур');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73558, 1, 0, 'Мы закрепимся здесь. Проведите разведку. Нужно выманить Гарроша из укрытия.', 14, 0, 100, 1, 0, 38950, 75239, 0, 'Король Вариан Ринн to Леди Джайна Праудмур');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73565, 0, 0, 'Дверь южной башни разбита!', 41, 0, 100, 0, 0, 0, 75327, 0, 'Башня to Специалист-взрывотехник');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73587, 0, 0, 'Все слышали приказ короля? Укрепить оборону! У героев должно быть время, чтобы добраться до Гарроша!', 14, 0, 100, 0, 0, 0, 76532, 0, 'Адмирал Тейлор');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73590, 1, 0, 'Мы очистим эту землю!', 12, 0, 100, 0, 0, 0, 74219, 0, 'Кор\'кронский вестник to Player');
