INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14873, 2, 0, 'Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it...', 12, 1, 100, 1, 0, 0, 10277, 0, 'Okla');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14873, 3, 0, 'Hmm... That rock is nice, but too small.', 12, 1, 100, 1, 0, 0, 10278, 0, 'Okla');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14873, 4, 0, 'This is a shiney rock. I\'ll put it in my bucket. Ok, I just need a few more...', 12, 1, 100, 1, 0, 0, 10279, 0, 'Okla');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14873, 5, 0, 'I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it.', 12, 1, 100, 1, 0, 0, 10280, 0, 'Okla');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14873, 6, 0, 'There... That should be enough rocks to make a necklace. I should probably get home now anyway.', 12, 1, 100, 1, 0, 0, 10281, 0, 'Okla');
