INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16059, 0, 2, 'I crush weaklings like you every day! Bring it on!', 14, 0, 100, 0, 0, 0, 11954, 0, '');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 0, 0, 'Do as I taught you!', 14, 0, 100, 0, 0, 8855, 13075, 3, 'Razuvious SAY_AGGRO #1');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 0, 1, 'The time for practice is over! Show me what you have learned!', 14, 0, 100, 0, 0, 8859, 13078, 3, 'Razuvious SAY_AGGRO #2');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 0, 2, 'Show them no mercy!', 14, 0, 100, 0, 0, 8856, 13076, 3, 'Razuvious SAY_AGGRO #3');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 1, 0, '%s lets loose a triumphant shout.', 41, 0, 30, 0, 0, 0, 13082, 3, 'Razuvious SAY_SLAY');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 2, 0, 'You should have stayed home.', 14, 0, 50, 0, 0, 8861, 13081, 3, 'Razuvious SAY_TAUNTED #1');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 2, 1, 'You disappoint me, students!', 14, 0, 50, 0, 0, 8858, 13077, 3, 'Razuvious SAY_TAUNTED #2');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 2, 2, 'I\'m just getting warmed up!', 14, 0, 50, 0, 0, 8852, 13072, 3, 'Razuvious SAY_TAUNTED #3');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 2, 3, 'Stand and fight!', 14, 0, 50, 0, 0, 8853, 13073, 3, 'Razuvious SAY_TAUNTED #4');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 2, 4, 'Sweep the leg... Do you have a problem with that?', 14, 0, 50, 0, 0, 8861, 13080, 3, 'Razuvious SAY_TAUNTED #5');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16061, 3, 0, 'An honorable... death.', 14, 0, 100, 0, 0, 8860, 13079, 3, 'Razuvious SAY_DEATH');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16204, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 0, 'Take that!', 12, 0, 100, 0, 0, 0, 12544, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 1, 'This isnt Silvermoon City! Where are we? Oh, I was confused there for a moment.', 12, 0, 100, 0, 0, 0, 12542, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 2, 'I tell you now, when we get back to Farstrider Enclave there will be a full accounting of your actions, Ranger Valanna! You alone were responsible for our losses and Im certain that the captain will see it my way.', 12, 0, 100, 0, 0, 0, 12543, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 3, 'Im sure to receive a commendation upon our return. We may have suffered a few losses, but overall I think we did quite nicely, dont you, Valanna?', 12, 0, 100, 0, 0, 0, 12545, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 4, 'Did I not yell at them? Pull back! Pull back and regroup with suppressing fire!!', 12, 0, 100, 0, 0, 0, 12541, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 5, 'They should have listened to me.  Then Id receive all of the accolades that are my due!', 12, 0, 100, 0, 0, 0, 12540, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16218, 0, 0, '%s loses its link!', 41, 0, 100, 0, 0, 0, 12156, 3, 'Tesla Coil EMOTE_TESLA_LINK_BREAKS');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16218, 1, 0, '%s overloads!', 41, 0, 100, 0, 0, 0, 12178, 3, 'Tesla Coil EMOTE_TESLA_OVERLOAD');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 0, 'You have got to be the most incompetent officer I have ever met!', 12, 0, 100, 0, 0, 0, 22242, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 1, 'Remind me once again why it is that I dont just leave?', 12, 0, 100, 0, 0, 0, 22241, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 2, 'Uh huh.  Whatever you say.', 12, 0, 100, 0, 0, 0, 22238, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 3, 'Idiot.', 12, 0, 100, 0, 0, 0, 22239, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 4, 'Would you please shut up?!', 12, 0, 100, 0, 0, 0, 22240, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 5, 'With all due respect, sir, stick it!', 12, 0, 100, 0, 0, 0, 22243, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 0, 0, '%s nods her head yes at what her advisor has to say.', 16, 0, 100, 273, 0, 0, 13362, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 0, 1, '%s disagrees with whatever it is that Valwyn has whispered to her.', 16, 0, 100, 274, 0, 0, 13363, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 0, 2, '%s seems nonplussed by whatever it is that her advisor has whispered in her ear.', 16, 0, 100, 6, 0, 0, 13365, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 1, 0, 'Executor, your direct assault approach will cost us more lives than I am comfortable with. A more nuanced strategy is called for; one that involves us reclaiming the surrounding territory, starting with the villages so as to cut off all points of reinforcement to Deatholme.', 12, 1, 100, 0, 0, 0, 12552, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 2, 0, 'I hear what you are saying, Mavren. Nevertheless, the final decision is mine. I appreciate your assistance, but the majority of the lives on the line are blood elf. I will not have those lives carelessly thrown away!', 12, 1, 100, 0, 0, 0, 12571, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 3, 0, 'How long until Forsaken reinforcements arrive? Our position here is tenuous. Your Lady promised us more soldiers. We must be able to concentrate solely on Deatholme and the Scourge!', 12, 1, 100, 0, 0, 0, 12557, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 4, 0, 'Mavren, I don\'t want excuses, I want results! The Farstriders aren\'t available and we\'ve received all that we\'re going to get from Silvermoon for now. I cannot ignore the Shadowpine trolls and Zul\'Aman... they\'re arrayed across our eastern border!', 12, 1, 100, 0, 0, 0, 12572, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 5, 0, 'No, I was right to begin with. Deatholme must come last. We must secure all of the Ghostlands first. I will not commit the forces here to a battle against Dar\'khan with enemies to our flank and rear!', 12, 1, 100, 0, 0, 0, 12562, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16231, 6, 0, 'Disagree with me all you like, High Executor. I will weigh your counsel, and then we will take the steps to free all of Quel\'Thalas as I deem necessary. We shall continue this discussion anon.', 12, 1, 100, 0, 0, 0, 12573, 0, 'Dame Auriferous');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 0, 0, '%s nods in agreement.', 16, 0, 100, 0, 0, 0, 12582, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 0, 1, '%s opens his mouth as if to add something, and then apparently thinks the better of it.', 16, 0, 100, 0, 0, 0, 12583, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 0, 2, '%s looks over at Advisor Valwyn with a smirk on his face.', 16, 0, 100, 0, 0, 0, 12584, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 0, 3, '%s grins at the High Executor\'s words.', 16, 0, 100, 0, 0, 0, 12585, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 0, 4, '%s appears bored with the whole discussion.', 16, 0, 100, 0, 0, 0, 12586, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 0, 5, '%s winks slyly at Advisor Valwyn.', 16, 0, 100, 0, 0, 0, 12587, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 0, 6, '%s fidgets with his armor, clearly uncomfortable at the tone of the discussion.', 16, 0, 100, 0, 0, 0, 12588, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 0, 7, '%s looks like he\'d rather be anywhere else but here.', 16, 0, 100, 0, 0, 0, 12589, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16251, 1, 0, '%s turns to Advisor Valwyn and winks lasciviously in her direction.', 16, 0, 100, 2, 0, 0, 13366, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 0, 0, 'With all due respect, milady, you have not fought the Scourge as I have. The Lady appointed me to assist you in defeating Dar\'khan because of that, and you would do well to consider what I have to say.', 12, 1, 100, 0, 0, 0, 12553, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 0, 1, 'I strongly disagree. Dar\'khan is not going to get reinforcements because he\'ll be bottlenecked. Deatholme has only one way in and out. Your traitor was a fool to corner himself there, and I intend to exploit that weakness!', 12, 1, 100, 0, 0, 0, 12554, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 0, 2, 'This is war, Dame Auriferous, and in any war lives will be lost! The only areas that we need to secure are the two ziggurats. We will turtle in and take the Tower of the Damned and its master by brute force. Then you will have your Quel\'Thalas.', 12, 1, 100, 0, 0, 0, 12555, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 0, 3, 'And if your Farstriders were here to reinforce us, I might agree. But, they are busying themselves instead with the Shadowpine trolls on your eastern border. The longer we wait for them, the stronger the Scourge will become.', 12, 1, 100, 0, 0, 0, 12556, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 1, 0, 'I believe that you\'re making my argument for me, but yes it would be nice if we had more reinforcements. I would counter that it would be faster to pull the Farstriders from their senseless skirmishing with the trolls.', 12, 1, 100, 0, 0, 0, 12558, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 1, 1, 'Dame Auriferous, we are the reinforcements. It may come to pass that The Lady will send more men in time. Better that you convince Silvermoon City to muster all of its forces and put them at our disposal here.', 12, 1, 100, 0, 0, 0, 12559, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 1, 2, 'You are correct; we must concentrate on the Scourge. Ignore the trolls and the villages, and focus our attentions on Deatholme! With Dar\'khan defeated you will find that the rest of the Scourge \'body\' will fall quickly, lacking their \'head\'.', 12, 1, 100, 0, 0, 0, 12560, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 1, 3, 'Forsaken reinforcements? Undercity is stretched thin with the Scourge on all sides. No, this will be a matter largely dealt with by the blood elves. We Forsaken are here as backup and advisors.', 12, 1, 100, 0, 0, 0, 12561, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 2, 0, 'Again I strongly disagree with your view of the strategic situation. Your plan will spread our forces too thin. It will leave us with only a small force to assault Deatholme. I cannot in good conscience execute such a strategy.', 12, 1, 100, 0, 0, 0, 12567, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 2, 1, 'Spread throughout the Ghostlands, our forces will be of little use in an assault on Deatholme. No, milady, I am here to kill Dar\'khan and that is what I intend to do!', 12, 1, 100, 0, 0, 0, 12568, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 2, 2, 'We\'ve been over this before. Only a focused assault upon Deatholme will meet with success. These other targets are distractions that we can ill afford. I suggest that you inform your Captain Helios that he is to leave off his campaign against the Shadowpine trolls and focus solely on the Scourge.', 12, 1, 100, 0, 0, 0, 12569, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16252, 2, 3, 'Perhaps we should let the matter rest for the time being until your chef has prepared your meal? You look a bit piqued if you don\'t mind my saying. We can resume our discussion when you are feeling more yourself.', 12, 1, 100, 0, 0, 0, 12570, 0, 'High Executor Mavren');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16289, 0, 0, '%s whispers something in the Dame\'s ear.', 16, 0, 100, 0, 0, 0, 13361, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16289, 1, 0, '%s looks appalled at the Executor\'s tone!', 16, 0, 100, 0, 0, 0, 12574, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16289, 1, 1, '%s tries to busy herself with other matters.', 16, 0, 100, 0, 0, 0, 12575, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16289, 1, 2, '%s glares daggers in the direction of High Executor Mavren and Deathstalker Maltendis.', 16, 0, 100, 0, 0, 0, 12576, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16289, 1, 3, '%s lifts her nose and sniffs in response to the High Executor\'s reply.', 16, 0, 100, 0, 0, 0, 12577, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16289, 1, 4, '%s shakes her head in disbelief at what she is hearing out of the Forsaken\'s mouth.', 16, 0, 100, 0, 0, 0, 12578, 0, 'Advisor Valwyn');
