INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16289, 1, 6, '%s reddens furiously at what she is hearing in response to her lady\'s question.', 16, 0, 100, 0, 0, 0, 12580, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16289, 1, 7, '%s concentrates on the wall opposite her, clearly ignoring the looks of the Executor and his assistant.', 16, 0, 100, 0, 0, 0, 12581, 0, 'Advisor Valwyn');
