INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17843, 0, 0, 'I want all of your trackers out in search of the criminal, Matis the Cruel. I have given trusted agents of the Hand flare guns. Should you or one of your trackers see the flare, it means that an agent has found Matis and needs assistance. And remember, tracker, Matis is to be brought back to me alive.', 12, 7, 100, 1, 0, 0, 14454, 0, 'Vindicator Kuros');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17875, 0, 0, 'I am at your service, my lord.', 12, 7, 100, 0, 0, 0, 14453, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17875, 1, 0, 'It will be done, my lord.', 12, 7, 100, 1, 0, 0, 14455, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17875, 2, 0, '%s calls forth their bretheren.', 16, 7, 100, 22, 0, 0, 14456, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17875, 3, 0, 'Brothers and sisters, tonight we hunt! A blood elf known as Matis must be brought to justice! The first to find him gets one week\'s vacation aboard the Exodar!', 12, 7, 100, 1, 0, 0, 14457, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17875, 4, 0, 'Onward! The wilds of Bloodmyst await!', 12, 7, 100, 1, 0, 0, 14458, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17895, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Frenzy');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17918, 0, 0, 'Victory will be ours!', 14, 0, 100, 0, 0, 0, 15194, 0, 'Time Keeper');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17918, 0, 1, 'Carry on! Victory at all costs!', 14, 0, 100, 0, 0, 0, 15195, 0, 'Time Keeper');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17918, 0, 2, 'Continue the fight! Do not falter!', 14, 0, 100, 0, 0, 0, 15196, 0, 'Time Keeper');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17964, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 0, 0, 'There\'s no turning back now. Stay close and watch my back.', 12, 0, 100, 1, 0, 0, 14648, 0, 'npc_demolitionist_legoso SAY_LEGOSO_1');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 1, 0, 'There it is! Do you see where the large red crystal is jutting out from the Vector Coil? That\'s where I need to plant the first set of explosives.', 12, 0, 100, 25, 0, 0, 14649, 0, 'npc_demolitionist_legoso SAY_LEGOSO_2');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 2, 0, 'Cover me!', 12, 0, 100, 5, 0, 0, 14650, 0, 'npc_demolitionist_legoso SAY_LEGOSO_3');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 3, 0, 'It won\'t be much longer. Just keep them off me while I work.', 12, 0, 100, 69, 0, 0, 14651, 0, 'npc_demolitionist_legoso SAY_LEGOSO_4');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 4, 0, 'That\'ll do it! Quickly, take cover!', 12, 0, 100, 5, 0, 0, 14652, 0, 'npc_demolitionist_legoso SAY_LEGOSO_5');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 5, 0, '3...', 12, 0, 100, 0, 0, 0, 14653, 0, 'npc_demolitionist_legoso SAY_LEGOSO_6');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 6, 0, '2...', 12, 0, 100, 0, 0, 0, 14654, 0, 'npc_demolitionist_legoso SAY_LEGOSO_7');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 7, 0, '1...', 12, 0, 100, 0, 0, 0, 14655, 0, 'npc_demolitionist_legoso SAY_LEGOSO_8');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 8, 0, 'Don\'t get too excited, hero, that was the easy part. The challenge lies ahead! Let\'s go.', 12, 0, 100, 1, 0, 0, 14656, 0, 'npc_demolitionist_legoso SAY_LEGOSO_9');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 9, 0, 'What in the Nether is that?!?!', 12, 0, 100, 5, 0, 0, 14657, 0, 'npc_demolitionist_legoso SAY_LEGOSO_10');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 10, 0, 'Be ready for anything.', 12, 0, 100, 1, 0, 0, 14658, 0, 'npc_demolitionist_legoso SAY_LEGOSO_11');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 11, 0, 'Blessed Light! She\'s siphoning energy right out of the Vector Coil!', 12, 0, 100, 53, 0, 0, 14659, 0, 'npc_demolitionist_legoso SAY_LEGOSO_12');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 12, 0, 'Cover me, we have to do this quickly. Once I blow the support on this side, it will disrupt the energy beams and she\'ll break out! I doubt very much that she\'ll be happy to see us.', 12, 0, 100, 1, 0, 0, 14660, 0, 'npc_demolitionist_legoso SAY_LEGOSO_13');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 13, 0, 'I\'ve almost got it! Just a little more time...', 12, 0, 100, 0, 0, 0, 14661, 0, 'npc_demolitionist_legoso SAY_LEGOSO_14');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 14, 0, 'Take cover and be ready for the fight of your life!', 12, 0, 100, 5, 0, 0, 14662, 0, 'npc_demolitionist_legoso SAY_LEGOSO_15');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 15, 0, '3...', 12, 0, 100, 0, 0, 0, 14653, 0, 'npc_demolitionist_legoso SAY_LEGOSO_16');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 16, 0, '2...', 12, 0, 100, 0, 0, 0, 14654, 0, 'npc_demolitionist_legoso SAY_LEGOSO_17');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 17, 0, '1...', 12, 0, 100, 0, 0, 0, 14655, 0, 'npc_demolitionist_legoso SAY_LEGOSO_18');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 18, 0, 'Holy mother of O\'ros!', 12, 0, 100, 5, 0, 0, 14663, 0, 'npc_demolitionist_legoso SAY_LEGOSO_19');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 19, 0, 'I... I can\'t believe it\'s over. You did it! You\'ve destoyed the blood elves and their leader!', 12, 0, 100, 1, 0, 0, 14664, 0, 'npc_demolitionist_legoso SAY_LEGOSO_20');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 20, 0, 'Get back to Blood Watch. I\'ll see you there...', 12, 0, 100, 1, 0, 0, 14665, 0, 'npc_demolitionist_legoso SAY_LEGOSO_21');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18064, 0, 0, 'I\'ll crush you!', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
