INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18166, 0, 0, 'Do not burden A\'dal with mundane questions, $r.  This being\'s will is all that keeps our enemies from crushing this city.', 12, 0, 100, 0, 0, 0, 17238, 0, 'Khadgar');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18166, 1, 0, 'Show our guest around Shattrath, will you?  Keep an eye out for pickpockets in the Lower City.', 12, 0, 100, 0, 0, 0, 17266, 0, 'Khadgar');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18174, 0, 0, 'Lady Liadrin is bound to find a way. She must.', 12, 1, 100, 1, 0, 0, 14838, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18174, 0, 1, 'Is... it true, what I\'ve heard?  That it was the Sun King himself?', 12, 1, 100, 1, 0, 0, 14839, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18174, 0, 2, 'Still, the Blood Knights will endure through this loss. We have no other choice... we must find another source.', 12, 1, 100, 1, 0, 0, 14840, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18174, 1, 0, 'Who do they think they are?', 12, 1, 100, 1, 0, 0, 14820, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18174, 1, 1, 'I know what you mean... they whisper about us when they think we can\'t hear them. They\'ll get theirs.', 12, 1, 100, 1, 0, 0, 14821, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18174, 1, 2, 'Jealous, that\'s what they are. They\'re afraid to dedicate themselves to true power.', 12, 1, 100, 1, 0, 0, 14822, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18174, 1, 3, 'They simply fear what they cannot begin to understand.', 12, 1, 100, 1, 0, 0, 14823, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18175, 0, 0, 'Matters are so uncertain now that the chamber below is empty...', 12, 1, 100, 1, 0, 0, 14831, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18175, 0, 1, 'It\'s gone. I can\'t believe it\'s gone. Who would have thought we\'d be betrayed like this?', 12, 1, 100, 1, 0, 0, 14833, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18175, 1, 0, 'Our neighbors are positively laughable. Have you seen the way they turn up their noses at us?', 12, 1, 100, 1, 0, 0, 14815, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18175, 1, 1, 'The rangers certainly have a high opinion of themselves.', 12, 1, 100, 1, 0, 0, 14818, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18175, 1, 2, 'I\'m quickly tiring of the haughty attitude I receive from those grubby bowslingers outside.', 12, 1, 100, 1, 0, 0, 14819, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18203, 0, 0, 'Don\'t let them escape! Kill the strong one first!', 12, 0, 100, 0, 0, 0, 15002, 0, 'Murkblood Raider - On Aggro');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18207, 0, 0, 'Where do you think you\'re going? Kill them all!', 12, 0, 100, 0, 0, 0, 15003, 0, 'Murkblood Scavenger - On Aggro');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18230, 0, 0, 'The magister\'s going to kill me...', 12, 1, 100, 0, 0, 0, 15011, 0, 'Trayanise');
