INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20454, 0, 0, 'Prepare to enter oblivion, meddlers. You have unleashed a god!', 14, 0, 100, 0, 0, 0, 18443, 0, 'Nexus-King Salhadaar');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20551, 0, 0, 'I hope this damned thing works. *Araxes pounds on the portable transponder.*', 12, 0, 100, 1, 0, 0, 18256, 0, 'Agent Araxes');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20551, 1, 0, 'I\'m here, commander.', 12, 0, 100, 1, 0, 0, 18267, 0, 'Agent Araxes');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20551, 2, 0, 'We\'ve located the mark, commander. Ya-six and I have been separated. Requesting backup.', 12, 0, 100, 1, 0, 0, 18268, 0, 'Agent Araxes');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20553, 0, 0, 'Araxes, come in... Are you there Araxes? Visibility is nil. Warp storms blocking us.', 12, 0, 100, 1, 0, 0, 18249, 0, 'Image of Commander Ameer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20553, 1, 0, 'Copy. Status report, soldier.', 12, 0, 100, 1, 0, 0, 18250, 0, 'Image of Commander Ameer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20553, 2, 0, 'Copy that, Araxes. Backup is on the way. Hold your position. I repeat, hold your position.', 12, 0, 100, 1, 0, 0, 18269, 0, 'Image of Commander Ameer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20553, 3, 0, 'Ameer, over and out.', 12, 0, 100, 1, 0, 0, 18270, 0, 'Image of Commander Ameer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20555, 0, 0, 'Who dares place this affront upon the altar of Goc?', 14, 0, 100, 53, 0, 0, 20381, 0, 'Goc');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20555, 1, 0, 'Who dares rouse Goc from his restful slumber?', 14, 0, 100, 53, 0, 0, 20317, 0, 'Goc');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20555, 2, 0, 'Your name is as insignificant to me as the names of the thousands who have died under the might of Goc. I will crush you and $N', 14, 0, 100, 53, 0, 0, 20321, 0, 'Goc');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20564, 0, 0, 'Araxes! Send help! I\'m pinned down in the mines. I... I\'m not sure where I am exactly but I had the presence of mind to drop tracers on the ground behind me.', 12, 0, 100, 1, 0, 0, 18281, 0, 'Image of Agent Ya-six');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20564, 1, 0, 'I can\'t follow the tracers back out, too many flesh beasts in the way. Send someone in... Follow the tracers... Ya-six, out.', 12, 0, 100, 1, 0, 0, 18282, 0, 'Image of Agent Ya-six');
