INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23656, 0, 0, 'Good!  Good! You are still alive.  More fun for me!', 12, 0, 100, 1, 0, 0, 23538, 0, 'Dragonflayer Rune-Seer to Valgarde Captive');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23656, 0, 1, 'These outsiders are weak and easily crushed.', 12, 0, 100, 1, 0, 0, 23536, 0, 'Dragonflayer Rune-Seer to 0');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23656, 0, 2, 'They are hardier than I would expect for their size.', 12, 0, 100, 1, 0, 0, 23539, 0, 'Dragonflayer Rune-Seer to Valgarde Captive');
