INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 1, 'Everyone look, it\'s $n!', 12, 0, 100, 0, 0, 0, 24087, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 2, 'Dawnblade, demons and naga beware, $n is back.', 12, 0, 100, 0, 0, 0, 24088, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 3, '$G Milord : Milady;, $n, we are fortunate to have you here with us.', 12, 0, 100, 0, 0, 0, 24089, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 4, 'The Shattered Sun Offensive is lucky to have $n on our side.', 12, 0, 100, 0, 0, 0, 24098, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 5, 'If it weren\'t for $n, we\'d all be long dead.', 12, 0, 100, 0, 0, 0, 24099, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 6, '$n, thank you again for helping us. We couldn\'t have achieved any of this without you.', 12, 0, 100, 0, 0, 0, 24100, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 7, '$Three cheers for $n!', 12, 0, 100, 0, 0, 0, 24102, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 8, 'It\'s an honor to serve with you, $n.', 12, 0, 100, 0, 0, 0, 24103, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 9, 'If I were one of those Dawnblade blood elves, I\'d be swimming away from the island now that $n is here.', 12, 0, 100, 0, 0, 0, 24106, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 10, 'How many wretched do you think $n has put out of their misery?', 12, 0, 100, 0, 0, 0, 24107, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 11, 'Snap to attention, $n, one of our finest, is here!', 12, 0, 100, 0, 0, 0, 24108, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 12, 'Welcome back, $n.', 12, 0, 100, 0, 0, 0, 24110, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 13, 'There\'s our $g hero : heroine; right there! How are you doing, $n?', 12, 0, 100, 0, 0, 0, 24111, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 14, 'I guarantee that without $n, this whole offensive would have been a failure.', 12, 0, 100, 0, 0, 0, 24112, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 15, 'No matter what, your efforts will always be exalted here, $n!', 12, 0, 100, 0, 0, 0, 24113, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 16, 'Lay waste to the demons on the Dead Scar lately, $n?', 12, 0, 100, 0, 0, 0, 24115, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 17, '$n, one of these days I want to buy you a drink.', 12, 0, 100, 0, 0, 0, 24116, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 18, '$n, you have no idea what a relief it is to see you!', 12, 0, 100, 0, 0, 0, 24119, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 19, 'What a coincidence... we were just talking about you, $n!', 12, 0, 100, 0, 0, 0, 24120, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 20, 'Who needs sentries? $n is here!', 12, 0, 100, 0, 0, 0, 24121, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 21, 'Hey, $n, try to leave some of the glory for us, would ya?', 12, 0, 100, 0, 0, 0, 24122, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 22, 'Do not fight the enemy unprepared, $n.  I can provide you with very powerful items... provided you have proven yourself to us.', 12, 0, 100, 0, 0, 0, 24302, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25032, 0, 23, 'I don\'t even want to think about what\'ll happen here if $n ever decides not to come back.', 12, 0, 100, 0, 0, 0, 24117, 0, 'Eldara Dawnrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25073, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25197, 0, 0, 'There, $n, now you will be able to speak with, and understand, the Winterfin murlocs.', 12, 0, 100, 1, 0, 0, 24428, 0, 'King Mrgl-Mrgl');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25211, 0, 0, 'Psst... $r, over here! Next to the dead whale.', 15, 0, 100, 0, 0, 0, 24450, 0, 'Cleaver Bmurglbrm');
