INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27390, 1, 0, 'Not... over... yet.', 14, 0, 100, 0, 0, 13230, 29534, 0, 'skarvald YELL_SKARVALD_DAL_DIED');
