INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29626, 0, 0, 'Delicious morsels have come to visit! Shall I use your bombs to cook you?', 14, 0, 0, 0, 0, 0, 30593, 0, 'Tormar Frostgut');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29643, 0, 0, 'This doesn\'t look pretty, lad.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 1');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29643, 1, 0, 'That right? Well why don\'t you prance your merry little self over there and grab them, eh?', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 2');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29643, 2, 0, 'That\'s strange... for a moment there, I could have swore you called me short.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 3');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29643, 3, 0, 'Toss me? Okay, that\'s it, punk.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 4');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29644, 0, 0, 'I don\'t know, Bailey... it\'s not so bad. We could throw a rock at the tools from here.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 1');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29644, 1, 0, 'Well... I was just thinking... you\'re a bit smaller, so you\'re less likely to set off any of those mines.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 2');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29644, 2, 0, 'No, no, no! Not short... it\'s just... well... maybe I could toss you across most of it?', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 3');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29644, 3, 0, 'Ahhh! That was close! You scared me.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 4');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29664, 0, 0, 'Grrrrr...', 0, 0, 100, 0, 0, 0, 0, 0, '');
