INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29677, 1, 0, 'You\'ve been seen! Use the net and ice slick to keep the dwarves away!', 42, 0, 100, 0, 0, 0, 30365, 0, 'Frosthound to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29677, 2, 0, 'The frosthound has located the thief\'s hiding place. Confront him!', 42, 0, 100, 377, 0, 0, 30368, 0, 'Frosthound to Player');
