INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29770, 1, 0, 'You dare?! Where is Baron Sliver? I would have words with him!', 12, 0, 100, 34, 0, 1173, 30458, 0, 'Lady Nightswood');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29775, 0, 0, 'Use of the Inventor\'s Disk detected. Emergency protocol gamma activated.', 12, 0, 100, 0, 0, 0, 30402, 0, 'Archivist Mechaton');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29775, 1, 0, 'Verifying status of Norgannon\'s shell.', 12, 0, 100, 0, 0, 0, 30403, 0, 'Archivist Mechaton');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29775, 2, 0, 'Norgannon\'s shell accounted for and secure. It will be available for transfer once user\'s identity has been verified.', 12, 0, 100, 1, 0, 0, 30404, 0, 'Archivist Mechaton');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29775, 3, 0, 'Standby to verify identity as Keeper Mimir.', 12, 0, 100, 1, 0, 0, 30405, 0, 'Archivist Mechaton');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29775, 4, 0, 'Identity verification failed. User is not Keeper Mimir.', 12, 0, 100, 25, 0, 0, 30406, 0, 'Archivist Mechaton');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29775, 5, 0, 'The Inventor\'s Disk has fallen into the hands of an unauthorized user.  Activating defense protocol.', 12, 0, 100, 0, 0, 0, 30407, 0, 'Archivist Mechaton');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29775, 6, 0, 'Impostor must be dealt with. The Inventor\'s Disk must be recovered.', 12, 0, 100, 5, 0, 0, 30408, 0, 'Archivist Mechaton');
