INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29851, 0, 0, 'You\'re the one that\'s been disrupting things? This should be easy.', 12, 0, 100, 0, 0, 0, 30465, 0, 'General Lightsbane');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29858, 0, 0, 'My dear general, your time has come!', 12, 0, 100, 0, 0, 0, 30470, 0, 'Lady Nightswood');
