INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (32642, 1, 0, 'Dis bad mojo what you doin\'. Don\'t say Mojodishu didn\'t warn ya!', 12, 0, 100, 1, 0, 0, 33028, 0, 'Mojodishu to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (32642, 2, 0, 'Really, mon? Just like dat you be leavin\' Mojodishu behind?', 12, 0, 100, 1, 0, 0, 33027, 0, 'Mojodishu to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (32687, 0, 0, 'There goes the finest $c I have the honor of knowing.', 12, 0, 100, 0, 0, 0, 32866, 0, 'Linda Ann Kastinglow');
