INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33356, 0, 0, 'I want that one killed! The one right there that doesn\'t look like the others!', 14, 0, 50, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33356, 1, 0, 'Pathetic weakling.You will die quickly!', 12, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33389, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33456, 0, 0, 'You found me!It does\'t metter,$R,because you will die!', 14, 0, 100, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33459, 0, 0, 'This isn\'t honorable combat. I long to face my opponents in battle with my axe in hand!', 12, 0, 100, 0, 0, 0, 34269, 0, 'Orgrimmar Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33459, 1, 0, 'These are my enemies. YOU would be my enemy in battle! Your king has declared war on my kind!', 12, 0, 100, 0, 0, 0, 34270, 0, 'Orgrimmar Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33459, 2, 0, 'But instead of fighting as my people would, as my people should, I find myself with a long, unwieldy pole, attempting to coax my wolf into charging my opponent without attacking.', 12, 0, 100, 0, 0, 0, 34271, 0, 'Orgrimmar Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33459, 3, 0, 'This is not how orcs fight! My duty to the Warchief wars with my personal honor. I hold no faith in your Light, but as you seem an advisor, I would hear your words.', 12, 0, 100, 0, 0, 0, 34272, 0, 'Orgrimmar Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33462, 0, 0, 'I don\'t know if I can compete! Everyone is so much bigger!', 12, 0, 100, 0, 0, 0, 34246, 0, 'Gnomeregan Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33462, 1, 0, 'I\'ve calculated the probability of my success, and it\'s distressingly low!', 12, 0, 100, 0, 0, 0, 34247, 0, 'Gnomeregan Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33462, 2, 0, 'I can\'t accurately assess the mathematics behind the charge of my turbostrider while keeping hold of my lance at the precise angle needed and keeping my shield in the exact location for maximum protection at any given second!', 12, 0, 100, 0, 0, 0, 34248, 0, 'Gnomeregan Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33462, 3, 0, '...and some of those mounts can trample me!', 12, 0, 100, 0, 0, 0, 34249, 0, 'Gnomeregan Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33465, 0, 0, 'I punched a penguin on my way in here.', 12, 0, 100, 0, 0, 0, 34252, 0, 'Undercity Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33465, 1, 0, 'Nah, not really. I just wanted to see the look on your face.', 12, 0, 100, 0, 0, 0, 34253, 0, 'Undercity Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33467, 0, 0, 'This fighting is endless. I\'m not certain how much more of it I can stand.', 12, 0, 100, 0, 0, 0, 34289, 0, 'Silvermoon Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33467, 1, 0, 'All of this posturing, all of this dancing about in rings... it seems pointless. What\'s the use?', 12, 0, 100, 0, 0, 0, 34290, 0, 'Silvermoon Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33467, 2, 0, 'And I\'m beginning to get HELMET HAIR. This is a travesty!', 12, 0, 100, 0, 0, 0, 34291, 0, 'Silvermoon Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33477, 0, 0, 'My heart hangs heavier with each passing day, Confessor.', 12, 0, 100, 0, 0, 0, 34260, 0, 'Stormwind Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33477, 1, 0, 'I have been ordered here, and here I stay and fight under the banner of Stormwind. But everything I do here is empty.', 12, 0, 100, 0, 0, 0, 34261, 0, 'Stormwind Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33477, 2, 0, 'Beyond these reaches, the Scourge continue their work, destroying more and more of what we\'ve built. The fight should be there. I should be there.', 12, 0, 100, 0, 0, 0, 34262, 0, 'Stormwind Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33477, 3, 0, 'How can I justify jousting while people die without my protection?', 12, 0, 100, 0, 0, 0, 34263, 0, 'Stormwind Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33480, 0, 0, 'Lass, I\'ve a confession to make to ya.', 12, 0, 100, 0, 0, 0, 34255, 0, 'Ironforge Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33480, 1, 0, 'I\'ve been feelin\'... urges.', 12, 0, 100, 0, 0, 0, 34256, 0, 'Ironforge Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33480, 2, 0, 'It was a powerful feelin\' while I was listenin\' to yer sermon, and it\'s drivin\' me nuts!', 12, 0, 100, 0, 0, 0, 34257, 0, 'Ironforge Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33480, 3, 0, '...I NEED A DRINK!', 12, 0, 100, 0, 0, 0, 34258, 0, 'Ironforge Champion');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33507, 1, 1, 'Out of our way, fools!', 1, 0, 100, 0, 0, 0, 0, 0, '');
