INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33642, 0, 0, 'Here I have amassed what may well be the greatest depository of lore in all of Outland. Even the grand libraries of Silvermoon City pale in comparison. Please... look, read, browse to your heart\'s content. You\'ll find everything organized and clearly labeled.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33695, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33785, 0, 0, 'Get off that horse and fight me man-to-man!', 14, 0, 100, 0, 0, 0, 34169, 0, 'yell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33785, 1, 0, 'I will not fail you, master!', 14, 0, 100, 0, 0, 0, 34185, 0, 'yell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34033, 1, 0, 'I said,SEARCH the bodies BEFORE you eat them!', 12, 0, 100, 0, 0, 0, 0, 0, 'OOC Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34033, 0, 0, 'Your flesh is for feit!', 12, 0, 100, 0, 0, 0, 0, 0, 'on Aggro Text');
