INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34249, 0, 0, 'You\'re finally here. Take the scenic route?', 12, 0, 100, 0, 0, 0, 34985, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34249, 1, 0, 'The exit is to the northwest... the mist will lead the way.', 15, 0, 100, 0, 0, 0, 34669, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34249, 2, 0, 'The mist will lead the way.', 15, 0, 100, 0, 0, 0, 34668, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34249, 3, 0, 'The Rune of Nesting is located across the bridge in the upper section of the southern chamber.', 15, 0, 100, 0, 0, 0, 34650, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34249, 4, 0, 'The Sapphire of Sky is located at the separate room in the lower section of the northern chamber.', 15, 0, 100, 0, 0, 0, 34651, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34249, 5, 0, 'The Black Feather Quill is located down the ramp in the lower section of the southern chamber.', 15, 0, 100, 0, 0, 0, 34652, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34249, 6, 0, 'The Raven Claw Talisman is located on the ledge in the upper section of the northern chamber.', 15, 0, 100, 0, 0, 0, 34653, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34249, 7, 0, 'Oben Rageclaw resides at the double bridges in the western chamber, near the hallway that leads out of the Barrow Den.', 15, 0, 100, 0, 0, 0, 34654, 0, 'Sentinel Huntress');
