INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34748, 2, 0, 'Blowing open my escape pod. I like your style, $n!', 12, 0, 100, 0, 0, 0, 35004, 0, 'Goblin Survivor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34748, 3, 0, 'What were those Alliance ships doing firing at us?! Did I see a Horde ship, too?', 12, 0, 100, 0, 0, 0, 35009, 0, 'Goblin Survivor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34756, 0, 0, 'Shh! Someone\'s here.', 12, 0, 100, 0, 0, 0, 35023, 0, 'Moriana Dawnlight');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34757, 0, 0, 'Don\'t get me wrong... I\'m grateful to have Shando Stormrage back, but this is all really suspicious. What happened to Fandral?', 12, 0, 100, 0, 0, 0, 35024, 0, 'Doranel Amberleaf');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34782, 0, 0, 'Do not trifle with me, $R.', 12, 0, 100, 0, 0, 0, 0, 0, 'on Aggro Text');
