INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34835, 0, 0, 'I was just comin\' to see you, Honest!', 12, 0, 100, 0, 0, 0, 0, 0, 'say Text on Aggro');
