INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34850, 0, 0, 'Stand ready, guards!  We don\'t know how many intruders we\'re dealing with, but the Headlands are overrun and we\'re cut off from the harbor towns.  Expect to be outnumbered.', 12, 0, 100, 1, 0, 19614, 0, 0, 'Prince Liam Greymane - Say 0');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34850, 1, 0, 'I want the perimeter secured and the gates manned by two guards at all times.  No one gets in, no one gets out.', 12, 0, 100, 1, 0, 19615, 0, 0, 'Prince Liam Greymane - Say 1');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34850, 2, 0, 'We protected Gilneas from the Scourge.  We protected Gilneas during the Northgate rebellion.  We will protect Gilneas from whatever this new threat may be.', 12, 0, 100, 25, 0, 19616, 0, 0, 'Prince Liam Greymane - Say 2');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34851, 0, 0, 'What... what are those things on the rooftops?', 12, 0, 100, 0, 0, 0, 39313, 0, 'Panicked Citizen to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34855, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34864, 0, 0, 'No one\'s allowed to leave. Prince Liam\'s orders.', 12, 0, 100, 1, 0, 0, 0, 0, 'Gilneas City Guard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34864, 1, 1, 'Stay calm. We have everything under control.', 12, 0, 100, 1, 0, 0, 0, 0, 'Gilneas City Guard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34864, 2, 2, 'It isn\'t safe to leave, there\'s something lurking out there in the woods.', 12, 0, 100, 1, 0, 0, 0, 0, 'Gilneas City Guard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34872, 5, 1, 'You want us go bash bad trolls?', 12, 0, 50, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34872, 5, 0, 'Useless! You are simply useless! I fear that we are all obligate to throw you into the Maelstrom!', 14, 0, 50, 22, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34872, 4, 0, 'I can\'t to believe the nerve of those trolls. Rebelling after all that I\'ve done for them!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34872, 3, 0, 'Chief, tell Chipie I\'ll kick him if  Kaja\'Cola  will get more later then that production has resumed.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34872, 1, 0, 'OUCH!', 14, 0, 100, 34, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34872, 2, 0, 'Okay, okay! I understand the message!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34872, 0, 0, 'It is always pleasant to receive a gift from the hand of the second in person. I\'m going to the baller immediately!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34874, 3, 0, 'Use your Hot Rod to run over Hired looters and get their loot.', 41, 0, 100, 1, 0, 0, 0, 0, 'say Text');
