INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36471, 1, 0, 'Remember, look for the pygmy leaders: Gaahl, Malmo, and Teloch.', 12, 0, 100, 1, 0, 0, 50081, 0, 'Foreman Dampwick to Player');
