INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36513, 0, 0, 'You think you\'re such a bigshot, saving everyone and making friends with these orc savages?!', 14, 0, 100, 0, 0, 0, 36460, 0, 'Trade Prince Gallywix to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36513, 1, 0, 'I\'m gonna crush you! I\'m the Trade Prince! Me! I gotta plan that\'s gonna get me off of this island and make me the king of Azshara!', 14, 0, 100, 0, 0, 0, 36461, 0, 'Trade Prince Gallywix to Player');
