INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38387, 2, 0, 'Those zombies are slow. You don\'t have to run right through them. Don\'t let them eat your brains, $n!', 12, 0, 100, 1, 0, 2304, 50013, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38387, 3, 0, 'Goblin grenades versus gnomish land mines. Show them who\'s better, $n!', 12, 0, 100, 1, 0, 0, 50095, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38387, 4, 0, 'I forget... did you take flying lessons back on Kezan?', 12, 0, 100, 6, 0, 0, 50097, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38387, 5, 0, 'Better hurry up, $n. I think that Thrall has news on where our people are being held.', 12, 0, 100, 273, 0, 0, 50099, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38387, 6, 0, 'First she left you for Chip, and now the Trade Prince! She was never good enough for you, $n.', 12, 0, 100, 274, 0, 0, 50114, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38387, 7, 0, '$n, get into the Ultimate Footbomb Uniform behind you and get down to the docks. Help Thrall kick the Trade Prince\'s butt!', 12, 0, 100, 1, 0, 0, 43948, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 0, 0, 'That\'s right. Look at the pretty flags.', 14, 0, 100, 5, 0, 0, 38652, 0, 'Goblin Survivor to B.C. Eliminator');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 1, 0, 'Have you heard of Azshara? There are no zombies there.', 14, 0, 100, 273, 0, 1473, 38647, 0, 'Goblin Survivor to B.C. Eliminator');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 2, 0, 'Smoker!', 14, 0, 100, 6, 0, 0, 38649, 0, 'Goblin Survivor to B.C. Eliminator');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 3, 0, 'We put a whole town... in a tiny little box!', 14, 0, 100, 0, 0, 0, 37356, 0, 'Goblin Survivor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 4, 0, 'Recycling! But, for stuff that\'s never been used before.', 14, 0, 100, 0, 0, 0, 37416, 0, 'Goblin Survivor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 5, 0, 'Stairways... for horizontal surfaces!', 14, 0, 100, 0, 0, 0, 37365, 0, 'Goblin Survivor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 6, 0, 'Tauren Paladins!', 14, 0, 100, 0, 0, 0, 37329, 0, 'Goblin Survivor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 7, 0, 'Mouse-sized beartraps... no... Bear-sized mousetraps!!', 14, 0, 100, 0, 0, 0, 37423, 0, 'Goblin Survivor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 8, 0, 'Flerberts. I\'m not sure what they do, but I\'d better lock down the name with a trademark.', 14, 0, 100, 0, 0, 0, 37417, 0, 'Goblin Survivor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 9, 0, 'Nose-stenders. For smelling things in different rooms.', 14, 0, 100, 0, 0, 0, 37353, 0, 'Goblin Survivor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38409, 10, 0, 'Giant gnomes. No, wait... tiny giants!', 14, 0, 100, 0, 0, 0, 37359, 0, 'Goblin Survivor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38432, 0, 0, 'You hear that, naga? $n is on $g his : her; way to settle our "negotiations"!', 14, 0, 100, 15, 0, 0, 49938, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38432, 1, 0, 'Everybody loves a pool pony. It\'s our fastest selling toy behind the Kablooey Bombs: Infant Edition.', 12, 0, 100, 1, 0, 0, 49951, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38432, 2, 0, 'That pygmy helm should fool them, at least for a little while. Get back to town!', 12, 0, 100, 25, 0, 0, 44571, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38441, 0, 0, 'Little help here!', 14, 0, 100, 0, 0, 0, 38767, 0, 'Ace');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38441, 1, 0, 'I never liked that guy. He stole your girlfriend. I say, good riddance!', 12, 0, 100, 273, 0, 0, 50108, 0, 'Ace to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38448, 0, 0, 'WHO DARES?!', 14, 0, 100, 15, 0, 15136, 38338, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38448, 1, 0, 'Little goblins? I remember when your race was created.', 14, 0, 100, 1, 0, 15139, 38346, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38448, 2, 0, 'I care nothing for your threats or these naga. Prepare to be removed from existence.', 14, 0, 100, 25, 0, 15140, 38349, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38448, 3, 0, 'Now, young $n, you will die!', 14, 0, 100, 0, 0, 15140, 38359, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38455, 0, 0, 'I got these little hellions on a short leash, $n. The naga won\'t attack us while we have their hatchlings.', 12, 0, 100, 1, 0, 0, 38356, 0, 'Ace to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38455, 1, 0, 'You ready to make their leader surrender, $g buddy : lady;? Okay, here we go.', 12, 0, 100, 1, 0, 0, 38357, 0, 'Ace to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38455, 2, 0, 'We\'ve got your hatchlings. You better not attack us if you know what\'s good for you!', 14, 0, 100, 5, 0, 0, 38375, 0, 'Ace');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38455, 3, 0, 'We know you were getting ready to attack us. You brought this on yourselves.', 14, 0, 100, 5, 0, 3434, 38377, 0, 'Ace');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38455, 4, 0, 'You naga keep your distance. We\'re not kidding around here!', 14, 0, 100, 5, 0, 3434, 38374, 0, 'Ace');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38455, 5, 0, 'Alright, naga leader, come on out from hiding and surrender in the name of $n and the Bilgewater Cartel!', 14, 0, 100, 5, 0, 0, 38381, 0, 'Ace to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38455, 6, 0, 'Um, dude, this does not look good. I\'m out of here!', 12, 0, 100, 5, 0, 0, 38358, 0, 'Ace to Faceless of the Deep');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38469, 0, 0, 'Enough!', 14, 0, 100, 0, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38469, 1, 0, 'Let\'s see how brave Gilneas gets on without its stubborn leader!', 14, 0, 100, 0, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38469, 2, 0, 'Such a waste.  That arrow\'s poison was not meant to be wasted on your whelp.  We\'ll meet again, Genn!', 12, 0, 100, 0, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Prince Liam Greymane');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38470, 0, 0, 'Block their retreat, Liam!  We\'ve got them right where we want them!', 14, 0, 100, 0, 0, 19606, 0, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38470, 1, 0, 'SYLVANAS!!', 14, 0, 100, 0, 0, 19606, 0, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38470, 2, 0, 'LIAM!!  NO!!!', 14, 0, 100, 0, 0, 19606, 0, 0, 'King Genn Greymane to Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38474, 0, 0, 'FATHER!!!', 14, 0, 100, 0, 0, 19606, 0, 0, 'Prince Liam Greymane to Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38474, 1, 0, 'We did it, father...', 12, 0, 100, 0, 0, 19606, 0, 0, 'Prince Liam Greymane to Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38474, 2, 0, 'We took back our city... we took back...', 12, 0, 100, 0, 0, 19606, 0, 0, 'Prince Liam Greymane to Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38507, 0, 0, 'Let us go at once.  We will track Sylvanas down.  For Gilneas!', 12, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38507, 1, 0, 'Follow Tobias Mistmantle into the Cathedral District.', 42, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38507, 2, 0, 'Let\'s do our best not to be seen, $n.', 12, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38507, 3, 0, 'Did you hear that, $n?  Sylvanas is headed to the cathedral.  Let\'s hurry!', 12, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38507, 4, 0, 'Quick!  Hide in the water!', 12, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38517, 0, 0, 'Okay. We\'re going to move away from the exploding volcano and try to hook up with the orcs for the final assault on the Trade Prince. See you on the other side... I hope.', 12, 0, 100, 1, 0, 0, 39240, 0, 'Slinky Sharpshiv to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38530, 0, 0, 'You can assure Garrosh that this is a minor setback.  Our victory in Gilneas will be absolute.', 12, 0, 100, 1, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38530, 1, 0, 'You\'d do well to watch your tone, General.  Neither you nor Garrosh have anything to worry about.  We\'ve ceased all production of the Plague, as he ordered.  We\'d never deploy it without his permission.', 12, 0, 100, 1, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38530, 2, 0, 'Go with honor, General.', 12, 0, 100, 1, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38530, 3, 0, 'What kind of question is that?  Of course we\'re deploying the Plague as planned!  Let the Gilneans enjoy their small victory.  Not even their bones will remain by tomorrow.', 12, 0, 100, 1, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38533, 0, 0, 'It appears you\'re losing control of Gilneas, Sylvanas.  Garrosh fears he\'s going to have to carry out this invasion himself.', 12, 0, 100, 66, 0, 19606, 0, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38533, 1, 0, 'You sound very confident, your Majesty.  I seriously hope you do not plan to use the Plague.  Garrosh has explicitly forbidden it.', 12, 0, 100, 66, 0, 19606, 0, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38533, 2, 0, 'I will deliver my report to our leader, then.  By your leave, my lady.', 12, 0, 100, 66, 0, 19606, 0, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38537, 0, 0, 'My Lady!  Should I order my men to stop the deployment of the Plague?  Or are we to continue as planned?', 12, 0, 100, 66, 0, 19606, 0, 0, 'High Executor Crenshaw to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38537, 1, 0, 'As you wish!', 12, 0, 100, 66, 0, 19606, 0, 0, 'High Executor Crenshaw to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38553, 0, 0, 'It\'s time to join the fray, $N!  With you on our side the scales will surely tip in our favor!', 14, 0, 100, 0, 0, 0, 38513, 0, 'Krennan Says');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 0, 0, 'What\'s this!?', 12, 0, 100, 5, 0, 17383, 38201, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 1, 0, 'He... he kept it? All this time, he kept it!?', 12, 0, 100, 5, 0, 17384, 38202, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 2, 0, 'I knew! ', 12, 0, 100, 5, 0, 17385, 38203, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 3, 0, 'I sensed a part of him still alive! Trapped... struggling... Oh, Arthas!', 12, 0, 100, 396, 0, 17386, 38204, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 4, 0, 'Perhaps - perhaps he might someday remember what he once was.', 12, 0, 100, 396, 0, 17387, 38205, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 5, 0, 'By the Light, may he at last find rest, free from the icy grip of that terrible blade.', 12, 0, 100, 396, 0, 17388, 38206, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 0, 0, 'Oh, lad...', 12, 0, 100, 396, 0, 17421, 38171, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 1, 0, 'How I miss those endless days in Lordaeron...', 12, 0, 100, 396, 0, 17422, 38172, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 2, 0, '...sharpenin\' yer skill with this dull blade...', 12, 0, 100, 396, 0, 17423, 38173, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 3, 0, '...forgin\' ya into a weapon meant to withstand the demands of a great destiny.', 12, 0, 100, 396, 0, 17424, 38174, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 4, 0, 'Heh... Yeh sure put them skills ta use, didn\'t yeh, lad?', 12, 0, 100, 6, 0, 17425, 38175, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 5, 0, 'If only I\'da been able ta stop yeh that day, how different things mighta been.', 12, 0, 100, 396, 0, 17426, 38176, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 6, 0, 'If only I\'da never discovered that accursed blade...', 12, 0, 100, 396, 0, 17427, 38177, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 7, 0, 'Farewell, Arthas... my brother.', 12, 0, 100, 396, 0, 17428, 38178, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 0, 0, 'Arthas...', 12, 0, 100, 396, 0, 17402, 38160, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 1, 0, 'Alas, hero of Azeroth, you give me a greater gift than you know.', 12, 0, 100, 396, 0, 17403, 38161, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 2, 0, 'Long have I struggled to forgive the prince for his terrible transgressions.', 12, 0, 100, 396, 0, 17404, 38162, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 3, 0, 'My soul has been wracked with unbearable anxiety, dark thoughts... distancing me from the Light.', 12, 0, 100, 396, 0, 17405, 38163, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 4, 0, 'I recall clearly the gleam of pride in his eye as he stood before me, eager to defeat the enemies of the Light...', 12, 0, 100, 396, 0, 17406, 38165, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 5, 0, 'Eager to defend his people, no matter the cost.', 12, 0, 100, 396, 0, 17407, 38166, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 6, 0, 'It is this memory of Arthas that I choose to keep in my heart.', 12, 0, 100, 396, 0, 17408, 38167, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 7, 0, 'I shall always be in your debt, friend.', 12, 0, 100, 396, 0, 17409, 38168, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 8, 0, 'Thank you.', 12, 0, 100, 0, 0, 17410, 38169, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 0, 0, 'So, it is done.', 12, 0, 100, 396, 0, 17376, 38179, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 1, 0, 'I had not dared to trust my senses - too many times has the Lich King made me to be a fool.', 12, 0, 100, 396, 0, 17377, 38180, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 2, 0, 'Finally, he has been made to pay for the atrocities he imposed upon my people.', 12, 0, 100, 396, 0, 17378, 38181, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 3, 0, 'May Azeroth never fail to remember the terrible price we paid for our weakness... for our pride.', 12, 0, 100, 396, 0, 17379, 38183, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 4, 0, 'But what now, hero? What of those freed from his grasp, but still shackled to their mortal coils?', 12, 0, 100, 6, 0, 17380, 38184, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 5, 0, 'Leave me. ', 12, 0, 100, 396, 0, 17381, 38185, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 6, 0, 'I have much to ponder.', 12, 0, 100, 396, 0, 17382, 38186, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 0, 0, 'Darion, my son.', 12, 0, 100, 396, 0, 17414, 38152, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 1, 0, 'At last, I am able to lay my eyes upon you again.', 12, 0, 100, 396, 0, 17415, 38153, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 2, 0, 'The Lich King tormented me without end, Darion.', 12, 0, 100, 396, 0, 17416, 38154, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 3, 0, 'Endlessly, he sought to break my will, to force me to serve him, to bind me to his blade...', 12, 0, 100, 396, 0, 17417, 38155, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 4, 0, 'Finally, when events demanded his full attention, he left me.', 12, 0, 100, 396, 0, 17418, 38156, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 5, 0, 'The one memory, I clung to, Darion... The one thought that kept me from giving in...', 12, 0, 100, 396, 0, 17419, 38157, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 6, 0, 'It was your sacrifice, my son, that again saved me from eternal peril.', 12, 0, 100, 396, 0, 17420, 38158, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38617, 0, 0, 'Make sure everything is ready.  The orcs\' emissary is ready to meet Sylvanas inside the cathedral.', 14, 0, 100, 1, 0, 19606, 0, 0, 'Forsaken General to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38643, 0, 0, 'I gotta get out of here before they catch me again!', 12, 0, 100, 5, 0, 0, 38569, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38643, 1, 0, 'I really can\'t thank you enough!', 12, 0, 100, 18, 0, 0, 38570, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38643, 2, 0, 'They took the others up into the volcano. Some to turn into zombies, some to sacrifice to their turtle god, Volcanoth!', 12, 0, 100, 5, 0, 0, 38571, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38643, 3, 0, 'The other captives are up in the volcano... they\'re being sacrificed, or worse, zombiefied!', 12, 0, 100, 5, 0, 0, 38572, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38643, 4, 0, 'I\'m free! I owe you my life!', 12, 0, 100, 4, 0, 0, 38565, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38643, 5, 0, 'I felt my life slipping out of me.', 12, 0, 100, 1, 0, 0, 38567, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38647, 0, 0, '$n, you gotta get in there and rescue our people!', 12, 0, 100, 5, 0, 0, 50007, 0, 'Izzy to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38647, 1, 0, 'Goblin zombies? The Town-In-A-Box has to be warned!', 12, 0, 100, 6, 0, 0, 50008, 0, 'Izzy to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38659, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38696, 0, 0, 'Echuta, gobin!', 14, 0, 100, 0, 0, 0, 38555, 0, 'Yngwie');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38696, 1, 0, 'Tay\'kn gobins!', 14, 0, 100, 15, 0, 0, 38560, 0, 'Yngwie');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38719, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38738, 0, 0, 'Get in there and show those zombies what the Bilgewater Buccaneers are made of, kid!', 12, 0, 100, 25, 0, 0, 50072, 0, 'Coach Crosscheck to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38738, 1, 0, 'You\'re going to love this!', 12, 0, 100, 273, 0, 0, 50083, 0, 'Coach Crosscheck to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38738, 2, 0, 'We\'re gonna make you the ultimate footbomb uniform, kid. But first, we need parts. Lots of them.', 12, 0, 100, 25, 0, 0, 50106, 0, 'Coach Crosscheck to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38745, 0, 0, 'Ooh, that\'s gotta hurt!', 14, 0, 100, 25, 0, 1473, 38645, 0, 'Kezan Citizen to B.C. Eliminator');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38745, 1, 0, 'Help me, help me!', 14, 0, 100, 0, 0, 0, 38768, 0, 'Kezan Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38745, 2, 0, 'Portable gasoline squirt-guns!', 14, 0, 100, 0, 0, 2304, 37337, 0, 'Kezan Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38766, 0, 0, 'Crowley!  You and your elven allies are hereby ordered to serve along the king\'s army.  Cursed or not, you are still bound by Gilnean law!', 12, 0, 100, 25, 0, 16419, 0, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38767, 0, 0, 'It is decided, then.  We will unite all Gilneans and drive the Forsaken from our lands.', 12, 0, 100, 0, 0, 16419, 0, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38768, 0, 0, 'Father!', 14, 0, 100, 0, 0, 19605, 0, 0, 'Lorna Crowley to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38781, 0, 0, 'You did it! We\'re saved!', 14, 0, 100, 1, 0, 16419, 0, 0, 'Gilnean Survivor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38855, 0, 0, 'Stay out of the way of Volcanoth\'s breath!', 41, 0, 100, 0, 0, 0, 38847, 0, 'Volcanoth to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38869, 0, 0, 'Hang on, $n! We have to get out of here, NOW!', 14, 0, 100, 0, 0, 0, 38874, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38880, 1, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38880, 0, 2, 'Raaar!!! Me smash $r!', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38880, 0, 1, 'Me smash! You die!', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38880, 0, 0, 'I\'ll crush you!', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38884, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38895, 0, 0, 'No. You\'re lying! My father will protect me!', 12, 0, 100, 0, 0, 0, 49337, 0, 'Lilian');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38910, 0, 0, 'You don\'t understand... I CAN\'T be undead! Not me, not now...', 12, 0, 100, 22, 0, 0, 38982, 0, 'Lilian');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38926, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38928, 0, 0, '$n, come on! Get over here!!!', 14, 0, 100, 0, 0, 0, 38908, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38930, 0, 0, 'I know dis is prolly busy work, but I don\'t mind. Dese baby raptors are cute lil devils.', 0, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38930, 1, 1, 'Try not ta make me look too bad, eh?', 0, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38940, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38949, 0, 0, 'You deserve to lose this land, for what you people did to us! Now back off, or face the wrath of the worgen!', 12, 0, 100, 1, 0, 0, 38896, 0, 'Infiltrator');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38999, 3, 0, 'Gebler, father, why would you...', 12, 0, 100, 1, 0, 0, 39001, 0, 'Lilian Voss');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38999, 2, 0, 'What? NO! This can\'t be! Gebler, you know me... we were friends once!', 12, 0, 100, 1, 0, 0, 38996, 0, 'Lilian Voss');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38999, 1, 0, 'Gebler, you came! What did he say?', 12, 0, 100, 1, 0, 0, 38994, 0, 'Lilian Voss');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38999, 0, 0, 'Yes, my... wait, be quiet! I hear the lieutenant approaching.', 12, 0, 100, 0, 0, 0, 39000, 0, 'Lilian Voss');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38999, 4, 0, 'The world of the living may have turned its back on me, but I\'m no damned Scourge. Just go.', 12, 0, 100, 1, 0, 0, 38999, 0, 'Lilian Voss');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39002, 3, 0, 'Unfortunately, you were too dangerous in life, and you\'re far too dangerous in undeath. I will enjoy killing you, you Scourged witch...', 12, 0, 100, 1, 0, 0, 38998, 0, 'Scarlet Lieutenant Gebler');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39002, 2, 0, 'The High Priest sends his regrets. He had hoped that one day you would be a powerful weapon against our enemies.', 12, 0, 100, 1, 0, 0, 38997, 0, 'Scarlet Lieutenant Gebler');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39002, 1, 0, 'High Priest Voss denounces you as a daughter. He\'s ordered that you be executed immediately.', 12, 0, 100, 1, 0, 0, 38995, 0, 'Scarlet Lieutenant Gebler');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39002, 0, 0, 'The time has come, my little captive... word has come back from your father.', 12, 0, 100, 0, 0, 0, 38993, 0, 'Scarlet Lieutenant Gebler');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39020, 0, 1, 'Kill the pig! Spill it\'s blood!', 14, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39020, 0, 0, 'Monsters! Monsters come to take our gold!', 14, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39038, 0, 0, 'Father!', 12, 0, 100, 0, 0, 0, 39138, 0, 'Lilian');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39038, 1, 0, 'Shut up.', 12, 0, 100, 0, 0, 0, 39140, 0, 'Lilian');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39038, 2, 0, 'You raised me to be a killer.  How am I doing, daddy?', 12, 0, 100, 0, 0, 0, 39141, 0, 'Lilian');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39038, 3, 0, 'But wait... I remember now. You taught me to only kill the undead. So you do want me to kill myself, daddy?', 12, 0, 100, 0, 0, 0, 39143, 0, 'Lilian');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39038, 4, 0, 'Then again, why kill myself... when I can kill YOU instead!', 12, 0, 100, 0, 0, 0, 39145, 0, 'Lilian');
