INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42697, 0, 0, 'None may challenge the Brotherhood!', 14, 0, 100, 0, 0, 5780, 6061, 0, 'Edwin van Cleef to Leader of Alliance');
